INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124545,"Diam-diam orang berkayuh","Kerana takut dikejar buaya","Saban malam abang mengeluh","Kerana adik sudah berpunya", 4, 6);
