INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124548,"Diam-diam orang melukis","Membuat gambar anak paladang","Dalam diam abang menangis","Niat meminang ditolak orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124549,"Diam-diam orang membenih","Kerana banyak beruk mengintai","Saban malam abang merintih","Kerana banyak niat tak sampai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124550,"Diam-diam orang menari","Kerana hari dah larut malam","Malam-malam abang menari","Kerana hati dan rindu dendam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124551,"Diam-diam orang menuba","Supaya ikan banyak yang mati","Dalam diam abang memuja","Semoga adik tidak membenci", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124552,"Diampai sama kering","Direndam sama basah","Digoreng sama garing","Berpisah sama gelisah", 4, 6);
