INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124561,"Dian tiga lilin pun tiga","Tanglong tergantong rumah laksamana","Diam juga sabar pun juga","Ada umor tidak ka-mana", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124562,"Diasap keris dengan sarungnya","Hujung terkena jari pun luka","Setiap yang arif tahu nilainya","Rasmi bunga kembang tak lama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124563,"Diatas atap di bawa-nya ginting","Itu ber-nama ruma Malayu","Brasa la dras engin ber-pusing","Legat tak legat saya pun tau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124564,"Diatas gunung burung kenari","Mau di tangkap harus mendaki","Karena kurang percaya diri","Lalu timbul hasad dan dengki", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124565,"Diatoer dengan doeri pandan","Gelombang besar membawanja","Melihat ajah pergi berdjalan","Entah 'pabila kembalinja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124566,"Diatua cupak nan duo","Dikaji kato nan ampek","Dalam tambo alah basuo","Paham di sinan kito pardapek", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124567,"Diatur dengan duri pandan","Ikan bercampur dengan tendeman","Antara pintu dengan halaman","Di situ adik abang tinggalkan", 4, 6);
