INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124569,"Diatur pula dengan kerusi","Tempat makan buah-buahan","Laksana bunga jadi peraksi","Seumpama kain jadi basahan", 4, 6);
