INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124571,"Diayak tepung dengan geragau","Cucur udang di atas meja","Lambaian kampung menggamit perantau","Pulang beraya bersama keluarga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124572,"Dibahan tidak tabahan","Balam nan tidak talambuangkan","Ditahan tidak tatahan","Malam nan tidak tatiduakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124573,"Dibakar cair itulah emas","Seurat benang emas tak susut","Di gigi air tuan dah lemas","Belum berenang di dasar laut", 4, 6);
