INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124575,"Dibalah lauik dibandakan","Ambiak rotan ka kutayo","Dibalah paruik dibanakan","Tidaklah tuan ka picayo", 4, 6);
