INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124583,"Dibancuh telur bercampur cuka","Jangan diinjak pantang dan larang","Akhirnya gugur bersepah juga","Dipijak orang pagi dan petang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124584,"Dibangun pondok lah duo tigo","Rimbo manjadi sabuah taratak","Martabat partamo di urang kayo","Sakali-kali usahlah tamak", 4, 6);
