INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124676,"Dicipta Allah tanpa ibu dan ayah","Manusia pertama dalam dunia","Krisis ekonomi menyebabkan rakyat susah","Ubatnya ialah sabar dan usaha", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124677,"Dicucur air juga selalu","Bunga berputik buah sebiji","Silalah kita berjanji dahulu","Janganlah mungkir ikrar dan janji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124678,"Dicuri oleh Ratu Sijuindu","Lalu dibuangkan ke dalam laut","Tiada kuasa menanggung rindu","Bagaikan hilang rasanya maut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124679,"Dicuri Ratu Cucuwindu","Ratu Daha empunya istri","Tiada kuasa menanggung rindu","Miskin pabatu badan sendiri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124680,"Dicuri ratu Sijuindu","Dibuangkan ke dalam laut","Tiada kuasa menanggung rindu","Bagaikan datang rasanya maut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124681,"Dicuri ratu Sijuwindu","Dibuangkan ke dalam laut","Tidak kuasa menanggung rindu","Bagaikan datang rasanya maut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124682,"Di-dalam gelap menchari galah","Terhantok batu terhenchut-henchut","Memang ta' silap memang ta' salah","Yang itu ia-lah kentut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124683,"Didalam mengabdi utamakan musyawarah","Membangun Bangsa bersusah payah","Contoh yang baik negara di Madinah","Yang di bina langsung Rasulullah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124684,"Di-dalam taman Binjaran Sari","Tempat permainan Galoh Genchana","Ayer sa-tegok boleh di-beri","Unggas datang dari-nya mana", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124685,"Didapat kapal tengah belayar","Orang Belanda konon isinya","Apalah hendak buat penawar","Adinda juga penawar bisanya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124686,"Didapati kering di musim panas","Mudah terbakar hutan terdekat","Baru jawi terus bernafas","Bila semua rakyat berminat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124687,"Didiami oleh bermacam-macam kehidupan","Istimewa sekali ialah manusia","Tujuan hidup bukan semata-mata makan","Tapi menyembah Allah Yang Maha Esa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124688,"Didik Mak Uteh","Dudok Kampong Kerinci","Kecik nampak puteh","Besaq lagi beseri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124689,"Didik selalu jumpa Pak Long","Tidak kira pagi petang","Adik kecik Pak Long sanjong","Adik besaq Pak Long lempaq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124690,"Didok ko na so adong sorkamu","Moldop do mata intjirmu","Didok ko na so adong rohamu","Montjong do panailimu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124691,"Didulang sadulang lai","Pandulang ameh bakarek","Disambuang uraian kaji","Iyolah paham nan ampek", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124692,"Didulang sadulang lai","Pandulang ameh Balingka","Diulang saulang lai","Panjapuik kaba nan tingga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124693,"Didulang sadulang lai","Pandulang ameh Manggani","Disambuang uraian tadi","'Gadang nan ampek' pulo dikaji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124694,"Die tiempa oejan dalam praoe","Rakit teomboe pandan seroenie","Sakitnja boejang sieapa kien taoe","Sakit la songgoe badan koe inie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124695,"Diea djoega moeda iyang gonda","Mangka die seboet toean kakenda","Sama kamarie diea botjanda","Sobatnja banja toean Olanda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124696,"Diedarkan percuma kepada peminat","Sajak berunsur dakwah Islamiah","Rakyat marhaen terus dikhianat","Nilai Ringgit Malaysia rendah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124697,"Difikir-fikir dijangka-jangka","Batang enau dikata nibung","Biarlah saya cuba meneka","Itulah lemang di dalam tabung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124698,"Digalakkan Al-Quran dan Hadis","Menuntut ilmu hingga ke China","Hidup cara Hedonis para gadis","Penagih dadah sebenarnya hina", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124699,"Digali dikeruk Sungai Pengerang","Banyak timbul si ikan buntal","Biar buruk di mata orang","Aduhai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124700,"Digali-gali pohon ketala","Digali dengan duri pandan","Dikaji-kaji hubungan kita","Rupanya adik mata duitan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124701,"Digali-gali urat pandan","Digali lalu ditimbuni","Dikaji-kaji untung badan","Dikaji lalu ditangisi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124702,"Digatok padi nyato lah masiak","Lalu diangkuik ka dakek lasuang","Lasuangnyo batu balariak","Talatak di dakek lumbuang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124703,"Digelar Cekel Waning Pati","Semar bergelar Si Rangga Jiwa","Sungguh hidup serasa mati","Yayi emas utama jiwa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124704,"Digelar oleh Betara Kala","Serta dengan ketujuh kedayan","Bercintakan tuan bagaikan gila","Tidakkah tuan belas kasihan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124705,"Digenggam takut mati","Dilepas takut terbang","Aduhai rusuhnya hati!","Nanar rindukan abang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124706,"Dighantau Bangkinang limo koto","Tabilang hebat Juang Datuok Tabano","Kalau ughang mamilio jodo","Yang taat samayang copek lakunyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124707,"Digoreng keli digulai puyu","Masak nasi kayu arang","Si telinga kuali mata kayu","Diasah besi menjadi parang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124708,"Dihadap kelana di paseban agung","Lalulah datang ketika barat","Kasih tuan kekanda tanggung","Redalah kekanda bersama mikrat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124709,"Dihadapan sekali Amerika Syarikat","Bertindak selaku polis dunia","Bila dasar negara ikut syariat","Membawa kebaikan kepada manusia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124710,"Dihampas si burung rangit","Dihampas beri kepisan","Berbantah bumi si dangan langit","Matahari turun memutus akan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124711,"Dihembus angin lembut dan tenang","Nampak nyaman suasana desa","Untuk Razmin kami meminang","Norhana Intan Watinisa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124712,"Dihembus bayu hujan tak lebat","Karam perahu di tengah lautan","Dipujuk rayu hati terlambat","Kasih berpadu paut-pautan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124713,"Dihidupkan pula oleh dewa-dewa","Bergelar Cekel Waning Pati","Bagaikan lenyap rasanya jiwa","Sungguh hidup merasa mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124714,"Dihilirnya Kampung Kelola","Bertentang dengan Tebing Tinggi","Di negeri kita semuanya ada","Asalkan sanggup berdikari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124715,"Dihinggapkan paksi rama-rama","Terbang Buraq dari udara","Hidup mati bersama-sama","Biar bersama porak peranda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124716,"Dihisap tembakau Bajang","Asapnya sampai ke Salida","Di mulut seperti sayang","Di hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124717,"Dihitung banyak tidak terkira","Apabila dijumlahkan jadi satu","Melompat ia seperti kera","Hanya tak pandai memanjat pintu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124718,"Diikat kata tandanya suka","Janji dikota kasih sejati","Setulus cinta pengubat duka","Jauh di mata dekat di hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124719,"Diiringkan anak perdana menteri","Bertemu orang menjerat rusa","Membuat gulana hati sendiri","Badan juga akan merasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124720,"Diiringkan oleh sampan borlah","Suka berlumba juga selalu","Melainkan mana takdir Allah","Permintaan janji dari dahulu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124721,"Diisi penyapu sambil dibilang","Sudah ditarek lalu pergi","Tiada suatu boleh menghalang","Buat baik juga dipuji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124722,"Diisok timbakau Bajang","Asoknyo sampai ka Salido","Di muluik bak cando sayang","Di ati sakali tido", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124723,"Dijaga rapi supaya selamat","Barang yang berat dibawa turun","Semasa bekerja mestilah cermat","Kerja yang berat janganlah gerun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124724,"Dijahit baju gunting selendang","Jangan dijahit tepi kain","Adik ragu kami pun bimbang","Jangan diganti dengan yang lain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124725,"Dijaik baju guntiang salendang","Nambek dikili tapi kain","Adiak ragu kamilah bimbang","Nambek diganti jo nan lain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124726,"Dijamin Allah pencipta alam","Berkuasa ke atas tiap-tiap sesuatu","Di waktu siang mahupun malam","Menghasilkan kerja-kerja  berkualiti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124727,"Dijamin Nabi masuk syurga","Yang beriman dan beramal soleh","Mengikut syariat seluruh keluarga","Yang halal saja dipulih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124728,"Dijawat oleh anak perdana","Bernama Raden Ratna Sari","Hatiku ruat bagaikan pana","Menaruh rawan sehari-hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124729,"Dijawati o!eh Seri Rama","Puan bertatah abang hempaskan","Hendak mati bersama-sama","Tuan tidak abang lepaskan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124730,"Dijawatkan oleh anak perdana","Bernama Raden Ratna Sari","Hatiku ruat bagaikan fana","Menaruh rawan sehari-hari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124731,"Dijual Cina kepada Melayu","Banyak membawa segala dagangan","Siang dan malam dendam terpayu","Adinda seorang jadi kenangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124732,"Dijual ke Negeri Kina","Dipunggah mari pekan Muara","Kerana tuan bijak laksana","Maka kami mahu bertanya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124733,"Dijunjung pada pengapit","Kiranya anak pasangan","Didukung pada orang sakit","Kiranya orang kekenyangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124734,"Dijunjung pada pengapit","Kiranya anak pasangan","Didukung pada orang sakit","Kiranya orang kekenyangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124735,"Dik ribut rebahlah padi","Dik cupak Datuk Temenggung","Hidup kalau tidak berbudi","Duduk berdiri serba canggung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124736,"Dikaik daun sapalapah","Ditinggakan sapaduo","Tujuah ratuih urang managah","Sakandak kita baduo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124737,"Dikait daun sepelepah","Ditinggalkan seperdua","Tujuh ratus orang mencegah","Sekehendak kita berdua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124738,"Dikail-kail tersangkut","Baik ditikam dengan lembing","Apa gunanya menaruh janggut","Jikalau janggut seperti kambing", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124739,"Dikaji adat nan ampek","Itu pusako urang Minang","Nan tuah cari sapakat","Kok nak cilako bueklah sialang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124740,"Dikaji kato sabaliaknyo","Kato kamanakan kapado mamaknyo","Atau murik pado gurunyo","Kato mandaki itu namonyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124741,"Dikaji pulo harato pusako","Pusako tinggi pusako randah","Pusako tinggi miliak basamo","Ka suklu lain tak buliah pindah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124742,"Dikaji undang nan duo baleh","Iyo tabagi kapado duo","Panyatokan kasalahan supayo jaleh","Anam tatuduah anam tacemo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124743,"Dikaji undang-undang nan salapan","Partamo banamo maliang curi","Maliang budi barang curian","Talalah takaja tatando tabeti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124744,"Dikaki bukit terdapat rawa","Disitu bermain burung merpati","Ingat muda sebelum datang tua","Ingat semasa hidup sebelum mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124745,"Dikalangan pelajar kita pupukkan","Masyarakat majmuk harus dihargakan","Dalam kepelbagaian dicari persamaan","Di semat cinta negara dan ketaatan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124746,"Dikalau tuan pergi ke Klang","Singga belabo di tanjung jati","Tuan menjadi burung terbang","Saya menjadi pohon yang tinggi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124747,"Di-kali-kali urek pandan","Di kali lalu di timbun-i","Di 'kaji-kaji untuang badan","Di kaji lalu di tangih-i.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124748,"Dikarang berjalur-jalur","Buah zaiton dari Kemboja","Terang seperti cermin hablur","Patut letaknya di atas meja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124749,"Dikarang dengan bunga orkid","Hiasan sanggol orang seberang","Rindu hatiku bukan sadikit","Nyaris mati terkejot sa-orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124750,"Dikarang dengan bunga rampai","Raksian kain nila utama","BiIa gerangan maksudku sampai","Hendak bertemu muda kesuma", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124751,"Dikarang hari 'lah siang","Orang memasak ikan tuna.","Memuji orang berterus terang","Sama saja dengan menghina.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124752,"Dikarang pula dengan berjalur","Buah zaitun dari Kemboja;","Terang seperti cermin hablur","Patut diletak di atas meja.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124753,"Dikarang pulak berjalur-jalur","Buah zaitun dari Kamboja","Terang laksana cermin hablur","Patut terletak di atas meja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124754,"Dikarang putri turun mandi","Dikarang hari 'lah datang.","Sungguh pandai tuan memuji","Membuat hati menjadi bimbang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124755,"Dikarang serta diatur","Tanjung Cendana kerasik putih","Mudah-mudahan jumpakan rasul","Selamat berjumpa dengan kekasih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124756,"Dikarang serta diatur","Tanjung Kindana kerasik putih","Mudah-mudahan jumpakan Rasul","Selamat berjumpa dengan kekasih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124757,"Dikarek rotan dilabiahkan","Dikarek lalu ka ureknyo","Padiah rangkungan minum santan","Ambiaklah racun kan ubeknyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124758,"Dikerat rotan dilebihkan","Dikerat sampai ke akarnya","Pedih rengkung meminum santan","Ambillah racun akan ubatnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124759,"Dikawal dijaga petang dan pagi","Demikian itu kerja Che' Amat","Jarang-jarang ditimpa rugi","Silalah tuan berjimat cermat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124760,"Dikayuh anak pagi hari","Naik ke darat membaling batu","Cari ilmu kenalkan diri","Dalam akhirat boleh membantu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124761,"Dikayuh anak pagi hari","Naik ke darat membaling batu","Cari ilmu kenalkan diri","Dalam akhirat boleh membantu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124762,"Dikelilingi oleh jutaan Arab","Kaya raya dengan minyak","Ukhuwah Islamiah mestilah akrab","Ikhwanul Muslimin mereka lanyak", 4, 6);
