INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124943,"Disusun bajak jo tingka","Bajak sawah hampir terlupa","Kalau dihitung jejak nan tinggal","Jatuh berderai si air mata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124944,"Ditabang batuang ka colok","Bari batampan ka anamnyo","Kok tak jadi buruang manyasok","Mangguguah sajo di dahannyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124945,"Ditabang batuang limo anam","Sutan di Ruhum diraoknyo","Tandonyo buruang ka j ahanam","Bungo baracun disasoknyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124946,"Ditafsirkan oleh ulama sejati","Seperti Ash-Shafie dan Al-Ghazali","Kesan ilmu dan bersih hati","Di Kolej Sri Ayu memantapkan diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124947,"Ditambah pulo kawajipan manti","Mambao parkaro kapado hakim","Ditiliak pulo sagalo sasi","Sarupo jaksa panuntuik hukum", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124948,"Ditampul bulung bira","Diparsaong bulung siala","Tona ni Nabinta","Ulang kita marhata sala", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124949,"Ditanak sipuluik bakapiek","Lah jadi katan tungkuih jo daun","Kok carai hiduik baponih surek","Kok carai mati taranggun-anggun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124950,"Ditanam bunga dibubuh baja","Disiram dipupuk setiap hari","Hari-hari ayah bekerja","Mencari nafkah anak isteri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124951,"Di-tanam kacang jo ampalu","Di-tanam di-dalam ladang","Anak-kanduang pulang daulu","Mandeh tar-agak malam siang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124952,"Ditanam oleh raja yang sakti","Permata intan dari kompeni","Hancur luluh rasanya hati","Ata-ata dengan begini", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124953,"Ditanam oleh raja yang sakti","Permata intan dari kompeni","Hancur luluh rasanya hati","Entah-entah dengan begini", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124954,"Ditanam oleh raja yang sakti","Permata intan dari kompeni","Sangatlah gundah di dalam hati","Entah-entah dengan begini", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124955,"Ditanam.jarak sama tinggi","Dang Serat bertanam serai","Di mana kan boleh berjarak lagi","Dunia akhirat tidak-bercerai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124956,"Ditangkap oleh hamba raja","Mari diikat dengan rotan","Haram tak boleh ditangkap sahaja","Bukan seperti binatang hutan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124957,"Di-Tanjong Pinang turunkan sauh","Singgah di-kota Raja Bedurai","Bila terkenang orang nan jauh","Air mata gugur berderai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124958,"Ditarah puntuang nan bakelok","Madang di lurah rapuah badan","Dikatokan untuang nan tak elok","Pintak jo pinto basalahan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124959,"Ditarah puntung yang berkelok","Medang di lurah rapuh badan","Dikatakan untung yang tak elok","Pinta dan in gin bersalahan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124960,"Ditarik akar ila-ilakan","Ditaruh akar bawah titian","Cari akal kira-kirakan","Jangan menyesal hari kemudian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124961,"Di-tatah dengan chermin China","Berkambi lagi bersengkuap","Mengerjakan titah sa-hina dina","Sa-isi negeri menjunjong sawab", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124962,"Ditebang betung lima enam","Sutan di Ruhum ditimpanya","Tandanya burung kan jahanam","Bunga beracun disesapnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124963,"Ditebang tidak tertebang","Ditebas juga kan jadinya","Diempang tidak terempang","Dilepas juga kan jadinya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124964,"Ditegaskan di zaman musa","Sungguh anak patuh kepada Ibu","Dianugerahkan hidup makmur sentosa","Allah jaga ketenangan qalbu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124965,"Ditegaskan di zaman musa","Sungguh anak patuh kepada Ibu","Dianugerahkan hidup makmur sentosa","Allah jaga ketenangan qalbu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124966,"Ditelan bulat dengannya biji","Setengah jatuh ke dalam sugara","Kening laksana berbentuk taji","Matanya bagai bintang jauhara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124967,"Ditengah huru hara","Hanya tekanan yang dirasa","Kita tak bisa bergembira","Karena terpaksa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124968,"Di-tengah rembang main bachok","Dudok berlima di-satu meja","Usah-lah abang lekas merajok","Adek-lah chuma mahu menduga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124969,"Di-tengah rembang mengail ikan","Dapat sa-ekor ikan kayu","Usah-lah abang kata demikian","Hamba sedia tolong membantu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124970,"Di-tengah rembang pergi menimba","Ambil kain di-buat tali","Apa-kah abang benchikan hamba","Sanggup na' kawin satu lagi?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124971,"Di-tengah sawah tumboh chendawan","Di-makan orang sa-tiap hari","Tidak-kah sedeh hatimu tuan","Jika hamba melarikan diri?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124972,"Ditenun kain berukir","Berjambul kelindan sutera","Kiriman raja dari Bantan","Benang emas buatan China", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124973,"Ditenun kain dengan kapas","Bermacam-macam warna ragi","Perahu lilin layar kertas","Berani kulayar lautan api", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124974,"Ditenun kain dengan kapas","Bermacam-macam warna ragi","Perahu lilin layar kertas","Berani kulayar lautan api", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124975,"Ditenunkan kain panjang lima","Diselimutkan oleh peranak Jawa","Masakah mana pulang bersama","Entahkan murka utama jiwa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124976,"Di-tepi kota tanam kelapa","Untok kenangan kemudian hari","Bercherai mata tidak mengapa","Asal jangan bercherai di-hati!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124977,"Di-tepi paya ayam berkokok","Terbang menginggap di-tepi prau","Buah sayah chuma sepokok","Jual beli sayah tak mau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124978,"Diterbangkan lantas ke dalam hutan","Hutan bernama Padang Saujana","Gundahnya abang bukan buatan","Hendak bertemu dengan mengerna", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124979,"Diterbangkan lantas ke dalam hutan","Hutan bernama padang saujana","Gundahnya abang bukan buatan","Hendak bertemu dengan mengerna", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124980,"Diterima rakyat yang patriotik","Dari Perlis hingga Sabah","Marah Bapa Borek Anak Rintik","Ulama' Hadhari kelihatan mengelabah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124981,"Diterima yang hati mulia","Bilal bin Rabah berkulit hitam","Beramal soleh sepanjang masa","Berdasarkan ilmu yang mendalam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124982,"Diterima yang waras fikiran","Di Timur Barat Utara dan Selatan","Bertemakan wanita arak dan hiburan","Berduyun-duyun ke perangkap syaitan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124983,"Ditiliak duduk hokum adaik","Ateh baniah nan duo baleh","Sarintiak kudaraik jo iradaik","Disigi sako mangkonya jaleh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124984,"Ditimbang sama berat","Diukur selangkah panjang","Cerita umpama ibarat","Bak lukah cipancang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124985,"Dititahkan manti jo dubalang","Barangkek ka Bukik Batu Patah","Datuak Suri Dirajo tak namuah parang","Kalau baparang nagari punah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124986,"Dititih batang bingkuang","Dipapek rang Pahambatan","Maah bapitih tigo uang","Dapeklah buah manggih utan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124987,"Dititi batang bengkuang","Dipepat orang Pahambatan","Berikan duit tiga uang","Dapatlah buah manggih hutan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124988,"Dititih titian tigo","Dititih badariak dariak","Tiok malam rasian tibo","Lai maraso bakeh adiak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124989,"Dititi titian tiga","Diinjak berderik-derik","Tiap malam mimpi tiba","Adakah terasa pada adik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124990,"Dititik batang berdengur","Ditutuk banir bertala-tala","Nan datang lah kami tegur","Lintuk leher dengan kepala", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124991,"Ditiup salong Muhammad","Ditiup mari di pintu gerbang","Dunia 'nak kiamat","Kasih sayang mari terbang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124992,"Ditolak arus sampah ke tepi","Dipukul badai kapal tak karam","Jujur lurus masih dikeji","Bagai gulai terlebih garam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124993,"Ditolak arus sampai ke tepi","Dipukul  gelombang kapal tak karam","Jujur lurus masih dikeji","Bagai rending terlebih garam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124994,"Ditukul batang ketakal","Tiganya batang keladi muyang","Elok diusul dengan diasal","Kita kan memang senenek semoyang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124995,"Ditulis dalam tulisan jawi","Di zaman Tun Sri Lanang","Berbeza sedikit perwatakan Mawi","Jasa ibubapa tetap dikenang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124996,"Diturunkan di bulan Ramadhan","Semasa Nabi sedang berkhalwat","Balik ke rumah dalam ketakutan","Tapi Khatijah menaikkan semangat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124997,"Ditutuh betung berdekak-dekak","Ayam berbunyi di bawah dapur","Sungguh beruntung orang pekak","Meriam berbunyi awak tertidur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124998,"Diubah nama Ken Patmawati","Dipeliharakan dengan sepertinya","Hanyalah adinda yang mengubati","Orang lain bukan patutnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124999,"Diukir dengan pisau wali","Dari pekan lalu berlabuh","Tinggallah tuan abang kembali","Pohonkan kain yang bekas tubuh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125000,"Diukur dijangka-jangka","Burung merak burung angkasa","Bertiup angin sangkakala","Di situlah kita berpandang mata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125001,"Diukur dijangka-jangka","Burung merak burung angkasa","Dengar tuan saya meneka","Layang-layang gagah perkasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125002,"Diulu gagak dihilir pun gagak","Gagak singgah dihujung canggong;","Putus pun masih hendak","Mana badan hendak menanggong.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125003,"Diurus dijaga tanam nan indah","Tempat berbaring puteri sepatang","Kelu pujangga hilangnya madah","Dakwah kering ilham tak datang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125004,"Di-waktu petang berjalan-jalan","Kaki ayam terkena duri","Kalau bagitu hajatmu tuan","Biar-lah hamba membawa diri!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125005,"Di-waktu petang mengail ikan","Dapat siakap dengan pelata","Pagar sekarang pagar tiruan","Jangan di-harap jangan di-perchaya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125006,"Diyakini oleh orang beriman","Nabi terakhir Nabi Muhammad","Jelas termaktub dalam Quran","Mahu kita terus berjihad", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125007,"Dja Sangkotan is aan het planten","Ambasang-blaren dwarrelen neer","Wanneer ik haar niet mocht krijgen","Zwaar ziek werd ik stellig nog eens", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125008,"Djalan djalan di piento radja","Tanam melatie di piengier kalie","Miengkien di larang mingkien sengang","Masa matie la doewa kalie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125009,"Djalan djalan diatas karang","Ada bale di piengier kalie","Main moeda djangan kapalang","Lebie bae troesa sekali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125010,"Djalan djalan ka roema obat","Petiek tjabe dengan tangkenje","Djalan djalan di roema sobat","Ambil setangan bekas pakenja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125011,"Djana galinggang djana garege","Djana tangki djana uwalang","Djana tangki djana pareme","Djana portahi djana ulubalang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125012,"Djangan blayar layar selapis","Kama karang batang aloean","Toendoek membatja dengan menangis","Atiekoe riendoe kepada toean", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125013,"Djangan die siempan diedalam atie","Antar sadja la dengan koeda","Maskie toe die boenoe matie","Saija tieda lepas die nona", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125014,"Djangan sentera kembang kembodja","Kembang kenanga saya petiekien","Djangan sentara di pintoe soega","Pintoe neraka saija nantieken", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125015,"Djangan takoet koeda menjebrang","Ikan jaroejoe pandjang doerienja","Djangan takoet toemangan orang","Asal pemoedjoe dalam atienja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125016,"Djangan tjerita sembarang orang","Dari saija ampunja jalan","Dari dehoeloe sampe sekarang","Blon oentoeng djadie pasangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125017,"Djieka moeda mandie di angke","Saya mandie di loea batang","Djieka Nonna mendjadi bangke","Saya djadie koeroeng batang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125018,"Djieka moeda mandie die laoet","Saya mandie di soengie angke","Djieka Nonna medjadie ramboet","Saya menjadi si boenga rampe", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125019,"Djieka Nonna membelie kapas","Daoen koentjie baroe terboeka","Pelok tjieoem bertarek napas","Sampe matie tida koe loepa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125020,"Djieka si toean njebrang ka bandang","Nonna bertanem la daon pandang","Djieka Nonna la maoe datang","Boe djalan dari belakang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125021,"Djieka si toean njebrang kakandang","Nonna bertanem la daon pandang","Djieka Nonna la marie datang","Bere djalan deri belakang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125022,"Djieka toean piara gangsa","Kasie makan roempoet singie sasie","Djieka Nonna toean bientjana","Djangan poean lepas die atie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125023,"Djieka toewan bedierie roema","Saya bedierie tiang daoeloe","Djieka Nonna menjadi boenga","Saya petiek tjieom dihoeloe", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125024,"Djika ada boenga melatie","Marie koe taroek dalam boseta","Ayoe nona la djantoeng atie","Ambil kries bertikam kita", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125025,"Djika ada daon angsana","Ambil loemoed di manga doewa","Sore-sore saya ke sana","Stoe slimoed kita berdoewa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125026,"Djika ada daon angsana","Ambil loemoet di manga doewa","Sore sore saya ke sana","Satoe slimoed kita berdoewa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125027,"Djika koe djalan sepandjang kalie","Soeda koe lihat poeloe melakka","Ilang bienie bole koe tjarie","Ilang sobat badan bienasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125028,"Djika toean djalan kahoeloe","Petiekein saija kembang kembodja","Djika toean mati di hoeloe","Toeggoe saija di pientoe soega", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125029,"Djika toean mandie ka laoet","Saya mandie di pingier piengier","Djieka Nonna mendjadie ramboet","Saya mendjadie la satoe siesier", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125030,"Do do tidur adik tidur","Makan ketupat hak mak kita beli","Kalau jodoh hamba tak syukur","Kalau tak dapat sama sekali", 4, 6);
