INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125302,"Duduk Panglima duduk menajat","Dari petang sampai ke pagi","Orang dah tua kalau memanjat","Tidak terlintas nak turun lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125303,"Duduk pembawa ia pun duka","Akan adinda intan mastika","Mana yang pantun mana seloka","Dibuatkan syair rajang ketika", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125304,"Duduk semayam di singgahsana","Berapa menteri ada mengadap","Bolehlah abang pergi ke sana","Supaya hati abang nan sedap", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125305,"Duduk tak duduk menggali lubang","Lubang tidak pasang pelita;","Duduk kita kenang ke orang","Orang tidak mengenang kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125306,"Duduk termenung di tepi laut","Sunyi di hati kian terasa","Hati yang marah jangan diturut","Kalau diturut rosak binasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125307,"Duduk-duduk dalam perahu","Menjala todak menjaring udang","Elok-elok adik menunggu","Bila tidak disunting orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125308,"Duduk-duduk di tepi perigi","Anak kodok dimakan haruan","Apa jadi kita begini","Bagai pungguk rindukan bulan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125309,"Duduk-duduk memintal benang","Akan ditenun menjadi kain","Tempat jauh lagi dikenang","Inikan pula tempat bermain.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125310,"Duduk-duduk orang berkumpul","Melihat ombak di tepi pantai","Elok-elok abang menyimpul","Bila tidak janji terungkai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125311,"Duduk-duduk tepi jambatan","Budak-budak menyimpai rotan","Apa disoal kepada badan","Sudah begitu jalan suratan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125312,"Duduknya itu di Kuala Pilah","Hampir dengan Batu Kikir","Orang sabar ditolong Allah","Jalan kejayaan hendaklah fikir", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125313,"Duhai lanang si paku lanang","Lanang mati di mabuk pinang","Karam di laut boleh direnang","Karam di hati bila nak senang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125314,"Duhai sahabat tercinta","Hancurkan berhala itu","Karena adanya berhala nista","Jahannam menanti tiada ragu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125315,"Duhai telang si bunga telang","Putih ungu berbelang-belang","Di mana tempat tiada helang","Belalang mengaku dirinya helang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125316,"Duit habeh satu peti","Cik Awang dudok saja","Cik Mek susah hati","Sebab dah ikut dia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125317,"Duit tidak ada","Kerana kalah judi","Kerja lain tak mau kerja","Kerja main judi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125318,"Duka tersimpan lamalah sudah","Baru dinilai baktinya bendang","Sembarang dihajat diperoleh mudah","Rasa bangga menjadi peladang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125319,"Duku lebat kuning mencelah","Dimakan burung datang sekawan","Betapa hebat ciptaan Allah","Sekecil nyamuk tidak terlawan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125320,"Duku masak kuning mencelah","Jatuh sebiji dirempuh angin","Cuti sekolah pergi berkelah","Penat belajar kita bermain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125321,"Dukung anak dari Melaka","Disambar jin di tepi kolam","Hutan terletak gunung permata","Cahaya melimpah serata alam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125322,"Dulang berisi buah-buahan","Ambar kesturi harum terlebih","Mengadap ingsun emas tempawan","Membawa persembah sebarang oleh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125323,"Dulang berisi buah-buahan","Ambar kesturi harum terlebih","Menghadap ingsun emas tempawan","Membawa persembah sebarang oleh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125324,"Dulang datang deri patani","Kapal busar deri palembang","Tidak blas mumandang kami","Suda miskin bangsa pun kurang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125325,"Dulang emas ditatah permata","Khazanah raja simpan di peti","Biar buruk wajah di mata","Asal pandai mengambil hati", 4, 6);
