INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125327,"Dulang emas tempat makanan","Ditutup dengan tudung saji","Sembah tiba pada si pangkalan","Kami nak walih hanya lai.", 4, 6);
