INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125330,"Dulang Palembang dari Jawa","Penuh sesak berisi kain","Terbang melayang rasanya jiwa","Niatku tidak pada yang lain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125331,"Dulang teletak brisi pulot","Pulot pulak berchampor padi","Suda tersala dari mulot","Jangan simpan di dalam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125332,"Dulang tembaga dari Siam","Buah cempedak di atas batu","Menaruh dendam dalam diam","Hati berkehendak saja begitu.", 4, 6);
