INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125334,"Dulang terletak atas bangku","Bangku tempat Belanda menyurat","Abang mati adikku pangku","Dengan kekanda bersama mikrat.", 4, 6);
