INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125336,"Dulang terletak di atas bangku","Bangku tempat Belanda menyurat","Abang mati adikku pangku","Dengan kekanda bersama mekrat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125337,"Dulang terletak di atas bangku","Bangku tempat Belanda menyurat","Abang mati adikku pangku","Dengan kakanda bersama mudarat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125338,"Dulang terletak di atas geta","Kelubi batang berduri","Orang tidak kasihkan kita","Minta perhamba dagang senteri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125339,"Dulang terletak di atas geta","Lada secawan bunga berembang","Sakit sungguh abang bercinta","Laksana bunga menantikan kumbang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125340,"Dulang terletak di atas kota","Kelubi batang berduri","Orang tidak kasihkan kita","Mintak perhamba dagang santeri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125341,"Dulang terletak di atas kota","Ada secawan bunga berembang","Sakit sungguh abang bercinta","Laksana bunga menantikan kumbang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125342,"Dulang terletak di atas meja","Lada secawan bunga berembang","Sakit sungguh kekanda bercinta","Laksana bunga menanti kumbang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125343,"Dulang terletak di atas meja","Lada secawan bunga berembang","Sakit sungguh kanda bercinta","Laksana bunga menanti kembang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125344,"Dulu anda pulang larut malam","Tiada seorang pun merasa terganggu","Sekarang anda semakin faham","Sudah ada istri menunggu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125345,"Dulu dah dok kampong","La ni dok bandaq","Dulu dok bawah tempurong","La ni dok dalam sangkaq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125346,"Dulu kereta sekarang bendi","Kalau bendi mana rodanya","Dulu suka sekarang benci","Kalau benci mana tandanya?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125347,"Dulu menjunjung si kayu arang","Kini malam seterang pelita","Adat berkunjung ke rumah orang","Beri salam izin dipinta", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125348,"Dulu muzium bercat merah","Kini sekarang bercat kuning","Bawa tersenyum manis bertambah","Kalau ku tauk nama ku sain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125349,"Dulu oghang lain","La ni oghang luaq","Dulu oghang pakai kain","La ni oghang pakai seluaq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125350,"Dulu sepasu kini berladang","Orkid diikat jual ke Jepun","Dulu membisu Cuma memandang","Kini sepakat bak serai serumpun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125351,"Dulu Tanjung Puteri","Kini Johor Bahru","Dulu disanjung isteri","Kini masyhur diburu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125352,"Dulu tebal hanyut ka lubok","Dulang di rumah pula dicari","Hilang akal kerana mabok","Hilang amanah kerana mencuri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125353,"Dulu timah sekarang besi","Layang-layang putus talinya","Dulu cinta sekarang benci","Sebab ada lain gantinya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125354,"Dum pak delima","Ketam padi pulut","Ditembak tak kena","Ditikam tak terlut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125355,"Dung do sitata sitarak i","Djolo sitanggis do so podom-podom","Dung do hita na marsarak i","Djolo tangis do anso modom", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125356,"dung solpu poda na lima","saroa hita nian marrosu","sahata saoloan","sapangambe sapanaili", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125357,"Dunia ada bolay di-pandang","Kenang menangis untong sindiri","Rumnia banyak bersukat gantang","Kundangan manis beritong biji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125358,"Dunia badunia urang Singgalang","Manembak buruang tabang tinggi","Mangandakan dadiah unto jalang","Balun ka dapek musim kini", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125359,"Dunia berdunia orang Singgalang","Menembak burung terbang tinggi","Menginginkan dadih unta jalang","Belum akan dapat musim kini", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125360,"Dunia ini harus dimakmurkan","Caranya dengan mengamalkan ilmu","Dunia diraih apa yang di karuniakan","Akhirat di isi ibadah tak jemu-jemu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125361,"Duo kali den ka Batipuah","Dapek pirangko duo-duo","Duo kali den kanai kicuah","Den etong balabo juo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125362,"Dua kali saya ke Batipuh","Dapat perangko dua-dua","Dua kali saya dikecoh","Saya hitung berlaba jua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125363,"Duo puluh limo nobi tabilang","Nobi Muhamad Rosul pilihan","Mati dahulu atau kamudian","Itu samuo korojo Tuhan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125364,"Duo puluo limo nobi tabiang","Nobi Muhamad Rosul pilihan","Mati doolu atau komudian","Itu somuo korojo Tuhan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125365,"Duo tigo gaduang di Padang","Sabuah gaduang tukang basi","Duo tigo bungo nan kambang","Satangkai sajo nan di ati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125366,"Dua tiga gedung di Padang","Sebuah gedung tukang besi","Dua tiga bunga yang kembang","Setangkai saja nan di hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125367,"Duo tigo lukah tahankan","Tali-tali banyak tabao","Kaduo baharato tapatan","Harato bini itu namonyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125368,"Duo tigo rumah di Padang","Namonyo rumah tukang basi","Duo tigo bungo nan kambang","Namonyo bungo pandak kaki", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125369,"Dua tiga rumah di Padang","Namanya rumah tukang besi","Dua tiga bunga yang kembang","Namanya bunga pandak kaki", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125370,"Duri landak tumbuh bertindih","Patah tangkai tunas semula","Takdir tidak kita berkasih","Sayangku bagai dua saudara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125371,"Duri rotan siapa pertajam","Kerakap-kerukup bunyi di hutan","Keroncong asli hiburan malam","Siaran rima jadikan pilihan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125372,"Durian banyak dibuat lempuk","Ikan belida dadanya panjang","Adat pinang pulang ke tampuk","Adat sirih pulang ke gagang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125373,"Durian baru dibuat lempuk","Lempuk dimakan bersama pulut","Sifat keji bagai tilam empuk","Nyenyak tidur Subuh pun luput!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125374,"Durian berdaon mainan budak","Lopak lopak berisi basay","Tuju taun di lambong ombak","Tidak lupa hati ku kasay", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125375,"Durian berdaon mainan budak","Lopak lopak berisi pasay","Tuju taun di lambong ombak","Tidak lupa hati ku kasay.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125376,"Durian daon dari hulu","Dapat sa-rangkay kebun chek yahya","Tuju taon ku adap guru","Kalu tak panday apa kan daya", 4, 6);
