INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125380,"Durian di ataih titi","Pisang di dalam dulang","Senang raa di hati","Ibu dah pulang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125381,"Durian dipalu-palu","Dipalu","dibuang jangan","Adik jangan diberi malu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125382,"Durian gugur habis dipungut","Pokok nipah banyak pelepah","Hilang jujur kerja bersungut","Gepoh-gapah meminta upah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125383,"Durian hilang duri","Nangka memang bergetah","Rindu membilang hari","Resah hatiku gundah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125384,"Durian jantung durian tembaga","Gugur sebiji dimakan bulan","Tidur siang simpan tenaga","Sembahyang tahjud jadi amalan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125385,"Durian masak tolong cekahkan","Jalan di bukit berliku-liku","Larangan Tuhan yang ditetapkan","Mempertentangkan keunggulan suku", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125386,"Durian putih bukannya kuning","Buahnya bulat lagi berduri","Mintakan ubat kepala pening","Ayuhai insun minta tambari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125387,"Durian salat berduri-duri","Oleh membeli dalam padian","Dayang Selat anak Si Nuri","Ia memajak kapal linguan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125388,"Durian segantang dibuat lempuk","Buah duku masak segugus","Liatnya hutang rebus tak empuk","Tahannya malu goring tak hangus", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125389,"Durian tembaga berbuah lebat","Gugur sebiji di laman jiran","Segak sasa pidatonya hebat","Jujur ikhlas diuji kebenaran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125390,"Durian tembaga dimakan tupai","Tupai melompat dahan ke dahan","Harum mewangi si bunga rampai","Baik campuran bunga pilihan", 4, 6);
