INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125641,"Emas merah kesuma dangding","Perahu sarat seperti lanting","Awaknya putih pinggangnya ramping","Matanya tajam seperti kucing", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125642,"Emas murni boleh diuji","Dibakar dipanas api","Sumpah setia pada janji","Apa pun akan terjadi", 4, 6);
