INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125648,"Emas sebungkal kilau terserlah","Cincin suasa gelang tembaga","Idea dangkal projek bermasalah","Habis masa habis tenaga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125649,"Emas semayam gelang di tangan","Dibawa mandi haram tak luntur","Siang menganyam angan-angan","Adat mimpi permainan tidur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125650,"Emas sepuluh bertatah permata","Dokoh labuh bertingkat tiga","Di lebuh raya banyak kereta","Duit tol pun banyak juga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125651,"Emas setahil perak berbungkal","Bawa ke medan ayam berlaga","Andai kail panjang sejengkal","Tergamak lautan hendak diduga?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125652,"Emas suasa dalam peti","Juga terisi berlian dan intan","Berbagai bangsa saling mengerti","Berdemokrasi amalan perikatan", 4, 6);
