INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125654,"Emas suasa emas bersadur","Dilebur dengan panasan api","Walaupun musang sedang tidur","Mengira ayam di dalam mimpi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125655,"Emas suasa mutu manikam","Berlian secepu jangan dibuang","Gila kuasa orang bertikam","Sanggup menipu kerana wang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125656,"Emas suasa nampak bersinar","Di atas peti cantik berseri","Jika berasa mengantuk benar","Elok berhenti rehatkan diri", 4, 6);
