INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (125658,"Emas urai dalam geleta","Kain pendukung koyak di bendi","Biar berurai air mata","Ayah kandung tidak peduli.", 4, 6);
