INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126381,"Gula madu chambor serbat","Mari minom s'orang sa-chawan","Jikalu malu boleh bersobat","Rimau di hutan boleh berkawan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126382,"Gula madu chambor serbat","Mari minom s'orang sa-chawan","Jikalu malu boleh bersobat","Rimau di hutan boleh berkawan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126383,"Gula madu champor serbat","Mari minom sa-orang sa-chawan","Jikalu malu boleh bersobat","Hariom di hutan boleh berkawan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126384,"Gula madu champor serbat","Mari minom sorang sa-chawan","Jikalu malu bolay bersobat","Arimo di hutan bolay berkawan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126385,"Gula madu satu kendi","Mari salin dalam loyang","Sedang madu lagi berbudi","Ini kan pulak kasay sayang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126386,"Gula masin kopi tawar","Anak dara menyanyi di dapur","Hilang bisa dapat penawar","Bagai kunyit dengan kapur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126387,"Gula melaka dibuat sarbat","Gula dimasak sebelum dibancuh","Baik kita tanya ke sahabat","Buruknya pula tanya ke musuh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126388,"Gula menitik nampak merata","Seperti juga manik urai","Harga setitik air mata","Bak mutiara jatuh berderai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126389,"Gula merah memengat sukun","Masam duku manisnya salak","Jelas arah cukup rukun","Islam itu agama akhlak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126390,"Gula nangka ada juga","Nasi masak kita makan","Gulai ada disimpan pula","Kalau ada tentu dikeluarkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126391,"Gula pekat dihurung lalat","Tajam sembilu membelah buah","Kerana pangkat sanggup menjilat","Hilang malu hilang maruah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126392,"Gula tongkol gula di bali","Makan anak raja di ulu","Bukan kah banyak musim dulu","Maka ingat dalam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126393,"Gula ulu bermasak pengat","Santan nya bernang masok chawan","Deri dulu misti ingat","Baru senang besok kemdian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126394,"Gula ulu di-masak pengat","Kasi makan kawan sekelian","Dari dulu kalu beringgat","Tentu selamt hari kemdian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126395,"Gula-gula mencari lege","Lege di cari di daun kayu","Gila-gila mencari tetek","Tetek ada di dalam baju", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126396,"Gula-gula pergi di Rembang","Sampai di sana menukar wang","Saya ini ibarat kembang","Segar di pakai layu di buang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126397,"Gulai asam bunga kantan","Halia kunyit penambah bahan","Semasa senang kawannya syaitan","Ditimpa susah mencari Tuhan!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126398,"Gulai ayam jeruk halia","Hendak pedas tambahkan lada","Mula-mula dipanggil belia","Kini bergelar rakan muda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126399,"Gulai berempah si-ubi keledek","Terkena kain turun warna-nya","Abang bersumpah di-depan adek","Pada yang lain haram hukum-nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126400,"Gulai daging nasinya hangat","Sayur petola rencahnya sotong","Mula berjoging penuh semangat","Minggu kedua sepatu digantung!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126401,"Gulai ikan keli","Ikan dari aloq","Makan tengah hari","Baru sedap tidoq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126402,"Gulai ikan tamban","Ikan dari kuala","Buat cabai belacan","Cicah ulam buah peria", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126403,"Gulai ikan tamban","Ikan dari kuala","Perangai macam setan","Hidup pun tak guna", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126404,"Gulai ikan tamban","Ikan dari kuala","Perangai oghang utan","Makan apa saja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126405,"Gulai Jawa terkenal manis","Dibawa orang dari Magelang","Yang ini cantik yang itu manis","Dasar abang mata keranjang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126406,"Gulai kangkong gulai gumbili","Jeruk kucai rendam di kudut","Selama tinggal di negeri ini","Hati usai menjadi kusut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126407,"Gulai lemak atau sempedas","Cukup seukur dengan rempahnya","Damai dunia bala pun lepas","Hidup akur dengan tuahnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126408,"Gulai lemak orang Kedah","Santan berair berisi kubis","Kenapa tamak menghimpun juadah","Tidakkah membazir bila tak habis?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126409,"Gulai lemak sayur cendawan","Anak udang goreng berlada","Kasih emak sepanjang jalan","Sayang saudara sama berada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126410,"Gulai masin nasi mentah","Dimakan dengan ulam kerdas","Eanggang lalu ranting patah","Tidak berlada terasa pedas!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126411,"Gulai panas nasi jelantah","Buah petola dimasak kari","Idea bernas patang dibantah","Cara sehala pentingkan diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126412,"Gulai pari campur halba","Ikan timbul lepas dituba","Idea baru berani mencuba","Kerja keras menunggu laba", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126413,"Gulai pariyo paik-paik","Apo dibuek parancahnyo","Taulan tamasyahua sampai ka langik","Mano sadikik paedahnyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126414,"Gulai peria pahit pahit","Apa dibuat perencahnya","Taulan termasyhur sampai ke langit","Mana sedikit faedahnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126415,"Gulai parpu tidak-lah pedas","Kalau pedas champor-lah gula","Bacha buku jangan-lah malas","Kalau malas kechiwa sudah-nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126416,"Gulai puyu menjamu fakir","Makan berulam dengan sambalnya","Tuah kayu di tangan pengukir","Idea kreatif halus seninya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126417,"Gulai selar sambal mangga","Api menyala si kayu arang","Rumput menjalar ke anak tangga","Pandai mencela halaman orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126418,"Gulai semilang hidang ke tamu","Nasi panas berulam jering","Duduk menimba lautan ilmu","Bertahun ditimba haram tak kering", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126419,"Gulai tenggiri nasi sekawah","Kuih nagasari dibawa belayar","Keliling negeri tuan berdakwah","Malang isteri jahil terbiar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126420,"Gulai timun santan kelapa","Masak lemak tengah hari","Kita berpantun bukanlah apa","Sekadar hendak menyukakan hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126421,"Gulaian di dalam tangan","Digulai dalam kuali","Kalau ada wang di tangan","Asal kuli jadi taukei", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126422,"Gulaian di dalam tangan","Digulai dalam kuali","Kalau inda ada wang di tangan","Asal taukei jadi kuli", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126423,"Gulainyo cukuik lauak pauak","Lah tasadio langkok-langkoknyo","Nan saratuih pamasak masuak","Linang guminang malah kuahnyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126424,"Gulama ikan di karang","Kail olay anak srani","Minkin lama di pandang kurang","Sebagi ayer pasang berbani", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126425,"Gulama ikan di-karang","Kail oleh anak serani","Makan lama bertamba kurang","Sebagi ayer pasang perbani", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126426,"Gulang-gulang nak rang Piaman","Maota sambia ka lambah","Buruang banamo ganggo aman","Makan jarek ba ulam gatah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126427,"Gulang-gulang nak rang Piaman","Bersembang sambil ke lembah","Burung bernama ganggo aman","Makanannya jerat berulam getah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126428,"Gulatek di ikat tali rami","Dahon kaladi di muka pintu","Jangan di chinta sobat yang rami","Hati di dalam jadi tak tuntu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126429,"Gulatek ka bukit bahru","Tapay di jual budak budak","Sunggu kembang tidak berbau","Bunga rampay terpakay jugak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126430,"Gulatek ka bukit bahru","Tapay di jual budak budak","Sunggu kembang tidak berbau","Bunga rampay terpakay jugak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126431,"Guliga di-puncha kain","Orang bertanak di-bawah Sentul","Masok shurga Jannatun Naim","Ini-lah anak baginda Rasul", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126432,"Guling batu air mengalir","Batu licin tidak berlumut","Hidup ramah bertingkah baik","Banyak sahabat nasib pun untung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126433,"Guling batu dorong ke kali","Kodok lari masuk ke air","Jangan harapkan dunia adil","Kalau anda ingin berhasil", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126434,"Guling tangan di kedai Kina","Oleh membeli di Negeri Acheh","Sapu tangan saya empunya","Selamat pakai jiwa kekasih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126435,"Gulinggang rang Mandi Angin","Bajabak bajambua tidak","Marintang bak ka camin","Nampak lai basuo tidak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126436,"Gelinggang orang Mandi Angin","Peti kecil berjambul tidak","Bagai melihat ke cermin","Tampak ada bersua tidak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126437,"Gulintangan di kedai Cina","Oleh membeli di negeri Aceh","Sapu tangan saya punya","Selamat pakai jiwa kekasih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126438,"Gulong tikaq sana-sini","Bawak hantaq dengan perahu","Tokey besaq nak ndak laki","Bejalan malam tidak menentu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126439,"Gulung tilam di tengah halaman","Tinggi mahligai Datuk Keramat","Anak Indung terlepas dari tangan","Alhamdulillah anak Indungku selamat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126440,"Gumgam-gumgam bunyi pelek","Macam oghang punggah rumah","Rupanya bujang kene cekek","Maih pusaka penyamun sesah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126441,"Gumi paer endeqna taoq","Miaq bale suruqna tukang","Lamun pelungguh melet nenaoq","Silaq dateng ojok bale tiang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126442,"Guna kuasa untuk dapat projek","Halal haram belakang kira","Beramal soleh tanda orang cerdik","Kerja-kerja kebajikan buat segera", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126443,"Gunakan parang buluh diukur","Buat galah pergi ke desa","Ada yang curang tidak bersyukur","Dilaknat Allah sepanjang masa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126444,"Guni padi penuh berisi","Esok lusa hendak digalas","Namun budi yang tuan beri","Terasa berat hendak membalas", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126445,"Gunong daik buchabang tiga","Inchek mat mudek kahulu","Budi baik di kunang juga","Kunang sampie zaman dolu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126446,"Gunong daik gunong di bantan","ketiga dengan gunong tanda","Lagi dahulo permata intan","sekarang ini batu belanda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126447,"Gunong di laut bertentang jati","Kota di Daik sahya pagar-kan","Hidop kita lawan-nya mati","Nama baik mau tinggal-kan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126448,"Gunong di laut bertentang jati","Kota di Daik sahya pagar-kan","Hidop kita lawan-nya mati","Nama baik mau tinggal-kan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126449,"Gunong di laut bettentang jati","Kota di Daik saya pagar kan","Idop kita lawan nya mati","Nama baik mau tinggal kan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126450,"Gunong Katak malindong Pangkor","Nampak lagi puloh sembilan","Sala di latak bagi di ator","Sebagi chin-chin permata selan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126451,"Gunong laut bertentang jati","Kota di daik saya pagar kan","Idop kita lawan nya mati","Nama baik mau tinggal kan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126452,"Gunong laut bertentang jati","Kota di Daik saya pagar kan","Idop kita lawan nya mati","Nama yang baik mau tinggal kan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126453,"Gunong Murut nampaknya tinggi","Nampak hitam serta melintang","Kalau ku turut hati berani","Laut yang dalam boleh ku renang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126454,"Gunong rapat gunong bertuah","Ramai penduduk tidak terperi","Sudah dapat gading bertuah","Kenapa tandok ta' berguna lagi?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126455,"Gunting bunga beribu ribu","Bawak kahulu pergi berdagang","Tidak kuasa menanggong rindu","Dari dulu sampai sekarang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126456,"Gunting dilipat terus","Rapuh menanti patah","Penting cepat diurus","Setia meniti perintah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126457,"Gunting kain kerawang baju","Jangan dirangkai keduanya","Sayangkan lain buangkan satu","Jangan dipakai keduanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126458,"Gunting mayang bunga selasih","Rotan itu ada di Lingga","Tuanku minta hambamu kasi","Bukan diberi la dengan arga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126459,"Gunting tersisip di bengkawan","Tangkainya patah atas bangku","Terlebih baik muda bangsawan","Ialah patut kekanda pangku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126460,"Gunting tersisip di mengkawan","Tangkainya patah di alas bangku","Terlebih baik muda bangsawan","lalah patut kekanda pangku", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126461,"Guntur berbunyi di hari hujan","Kilat jaga turut menyambar","Bicara sombong berlebihan","Kawan muak menjadi lawan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126462,"Gunuang Ledang tantang Malako","Tampak nan dari Kampuang Jati","Salamo tuan lupuik di mato","Ati nan tidak sanang lai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126463,"Gunuang Padang bajanjang batu","Salasiah badahan tidak","Basilang karih di pintu","Namun kasiah talarang tidak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126464,"Gunung Padang berjenjang batu","Selasih berdahan tidak","Bersilang keris di pintu","Namun kasih terlarang tidak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126465,"Gunuang Padang bajanjang batu","Sulasih badahan tidak","Tariak padang pancuanglah aku","Namun kasiah talarang tidak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126466,"Gunung Padang berjenjang batu","Selasih berdahan tidak","Tarik pedang pancunglah aku","Namun kasih terlarang tidak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126467,"Gunuang Padang batanggo janjang","Bari batanggo batu mandi","Manapi-napi bada baliang","Ma intai buah pauah janggi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (126468,"Gunuang Singgalang jo Marapi","Sumarak Minang pulau Paco","Dubalang tagak di pintu mati","Mamaliharo nagari sarato isinyo", 4, 6);
