INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128354,"Inaq mongkaqleq pawon","Amaq lalo njual salak","Akbar tefonis telu taon","Parane doang ndeqna salaq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128355,"Incang-incangan kotolo","Kotolo dang morarat","Atiku raos gilo","Monengar kato surat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128356,"Incang-incang ketolo","Ketolo sedang merambat","Hatiku rasa gila","Mendengar kata surat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128357,"Inche Abu membelah bukit","Sampai lalu berhenti makan","Angin menyerbu balek sedikit","Sepaya senang kita layar kan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128358,"Inche Aki orang-nya kurus","Menjual lada  dengan temikai","Kalau kaki sudah terperluas","Apa guna di-sudahkan lantai?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128359,"Inche Akil ashik bertanak","Khenduri besar nasi lemak","Masa kechil jadi anak","Sudah besar jadi-lah onak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128360,"Inche Akil penjual belanak","Ka-pulau besar membeli kelapa","Masa kechil menjadi anak","Sudah besar bagaimana pula?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128361,"Inche aku orang-nya kurus","Pandai berbudi ramah tamah","Tapak kaki ta' pernah haus","Tidak seperti besi dan timah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128362,"Inche Ali memblah buloh","Anak beranak dudok meraot","Kalu negri menjadi puloh","Lama lama hanyot ka-laot", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128363,"Inche Ali orang Kedah","Memakan nasi ulam pauh","Tentu sa-kali ta' ada faedah","Yang memberi juga si-bodoh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128364,"Inche Anas ka-pulau tiga","Naik motokar Inche Ismail","Berpanching emas umpan suasa","Sepat di-paya hamba na' kail", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128365,"Inche ani pergi ka-klang","Membawa nangka dengan betek","Pemudi berani berbaju belang","Sebab pemuda berbaju batek", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128366,"Inche anis menjual chinchin","Chinchin kelabu emas suasa","Jari manis di-sarong chinchin","Telunjok dan ibu menanti masa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128367,"Inche Awang berkain selimut","Mengintai dara sedang mandi","Pakai buang ta' pakai sangkut","Apa pula yang sa-pelek ini?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128368,"Inche baba orang china","Menjual berangan ka-rumah che' puan","Ingin hamba hendak bertanya","Siapa gerangan kekaseh tuan?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128369,"Inche Babjan mengoyak kertas","Kertas di-layang ka-atas bumbong","Kalau hujan ia-ka-atas","Itu-lah di-katakan payong", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128370,"Inche Bakar menggetah punai","Punai sepasang mengirai bulu","Berapa deras harus di-sungai","Berjumpa pasang mudek kehulu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128371,"Inche Bala main lelayang","Lelayang merah kepala naga","Tumpah gula semut kenyang","Jong pechah bagaimana pula?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128372,"Inche bidi ka-bengkaleh","Singgah di-hulu membeli ikan","Antara budi dengan kekaseh","Budi dulu hamba pentingkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128373,"Inche Bidi memikul guni","Guni berisi limau purut","Ular lidi menelan babi","Kenapa chaching bengkak perut?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128374,"Inche busu berkebun nenas","Pokok kopi di-tanam juga","Memang dah tahu api itu panas","Hati degil na' chuba juga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128375,"Inche Darus ka-kebun china","Beli belimbing serta daun sup","Isteri kurus ada tuah-nya","Ta' ada daging tulang ku buat sup", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128376,"Inche Daud memintal benang","Hendak mengikat kayu arang","Karam di-laut boleh kau renang","Karam di-hati menong sa-orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128377,"Inche Dollah mengambil karang","Karang di-ambil serberang sana","Bukan sala kepada orang","Diri sendiri buat binchana", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128378,"Inche Esah menanam padi","Taman mari di sawah China","Jangan suka mau berbudi","Dalam budi kerap terkena", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128379,"Inche Hassan orang di Pagor","Dudok sembahyang ditengah paya","Bila terkejut dari tidor","Terbayang bayang disangka dia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128380,"Inche Hussan menetak atap","Atap di-rendam ujong nya basa","Sekelian jempotan datang mengadap","Bawak gurindam yang halus bahasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128381,"Inche jama pergi ka-huma","Hendak menchari batu  sempadan","Usahlah lama berdagang ka-sana","Kelak binchana menimpa badan!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128382,"Inche Jama pergi ka-huma","Padi di-ketam di-buat berteh","Iklim sama negeri-nya sama","Elok-kah si-hitam berjumpa si-puteh?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128383,"Inche Kachong orang di pekan","Gunting bunga beribu ribu","Timbang rachun kasi ku makan","Tidak kuasa menanggong rindu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128384,"Inche Kader tukang lelang","Lelang mari berkodi kain","Kaseh jangan mudah di-buang","Takut susah charik yang lain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128385,"Inche kudus menebas belukar","Semak samun habis di-tebaskan","Kalau hangus bumi terbakar","Mati-lah hamba di-pangkuan tuan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128386,"Inche Malek berbaju belang","Di-atas pangkin duduok bergobek","Usah adek berhati walang","Abang ta' mungkin benchikan adek", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128387,"Inche malek orang budiman","Berbaju baru berkacha mata","Kasehan adek mendengar kelohan","Kenapa bagitu abang berkata!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128388,"Inche Mamu memakai tangkai","Tangkai tulang anak beruang","Apa-kah ilmu adek pakai","Hamba memandang bertambah sayang?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128389,"Inche Mamu pandai seloka","Pandai seloka dengan menari","Tuntut-lah ilmu jangan leka","Supaya berguna kemudian hari!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128390,"Inche marikan gagah perkasa","Menahan bubu di-kuala betong","Banyak ikan sudah ku pereksa","Tulang satu hanya-lah sotong", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128391,"Inche Moh Su pikol tahu","Tahu tumpa dalam puan","Laksana pikol-an di atas bahu","Berat ringan saya yang menahan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128392,"Inche Mutu pandai bercherita","Bercherita fasal naik haji","Kalau bagitu adek berkata","Mari-lah kita memikat janji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128393,"Inche nasib menbacha buku","Buku cherita orang judah","Apa-kah nasib untong badanku","Gulai sedap nasi mentah?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128394,"Inche Nayan menahan lokah","Dapat sepat dengan banyak-nya","Kalau tempayan sudah pechah","Ta' kan dapat di-sembunyikan-nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128395,"Inche puan ka-singapura","Singgah berubat ka-bukit senyum","Tolong tuan ubatkan segera","Hati terpikat kerana senyum!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128396,"Inche Puan menetak palas","Lalu menetak batang ber duri","Budi tuan belom di balas","Sebagai embon jatoh ke Bumi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128397,"Inche' puteh berbaju jarang","Potongan rambut pendek pula","Masakan kaseh ta' di-rampas orang","Sebab bunga tidak di-bajakan-nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128398,"Inche Saba memasak lokan","Di-champor kichap dengan lada","Bagaimana hamba hendak makan","Kerana kachip tidak ada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128399,"Inche Saba sembahyang suboh","Bagitu-lah kerja sa-tiap hari","Bukan hamba berpelok tuboh","Tapi mengenang malang-nya diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128400,"Inche Sabtu bermain lembing","Di-tengga laman dudok berdiri","Lagi di-batu lagi di-benteng","Supaya jangan rosak negri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128401,"Inche Sani memakai tudong","Tudong terendak buatan-nya lawa","Patah hati inging bergantong","Orang ta' hendak apakan daya!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128402,"Inche sani menahan bubu","Terkena sengat luka kaki-nya","Teman di-sini usah-lah rindu","Hanya teringat pada kanda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128403,"Inche sani menembak keluang","Keluang tergantong di-pohon pala","Hamba di-sini leka berjuang","Tuan di-kampong bagaimana pula?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128404,"Inche Seeti orang Semarang","Handak mandi pergi kehulu","Tabek kechuali orang sekarang","Mulotnya manis hati-nya kaudu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128405,"Inche Shaugi penyanyi ternama","Suka berdendang lagu asli","Abang pergi memikul senjata","Doa'lah abang lekas kembali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128406,"Inche Siti tidor di lengan","Bangon pagi dudok bersenam","Ayam di potong tidak termakan","Gajah mati mahu di tanam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128407,"Inche Suboh dudok di pintu","Dudok menjeling trendak tudong","Orang bodoh memang begitu","Fikeran hati menchapai gunong", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128408,"Inche Sufian menchari jerami","Hendak di-rendam di-dalam pasu","Kalau demikian rupa-nya suami","Boleh ku tukar giri palsu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128409,"Inche Suri naik perahu","Singgah bermain di-kampong Jaha","Duhai saudari hamba nak tahu","Kenapa kain koyak di-paha?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128410,"Inche tapa ka-kuala kangsar","Menonggang kuda sampai ka-situ","Hasotan siapa kanda mendengar","Sanggup kanda kata bagitu?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128411,"Inche Uda berlayar senja","Hanyut sampan-nya di-bawa arus","Banyak benda dalam dunia","Benda apa yang tidak haus?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128412,"Inche uda ka-asam kumbang","Menonggang kuda tali pedati","Wahai bonda  usah-lah bimbang","Nasihat bonda tersemat di-hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128413,"Inche Uda ka-rumah Ismail","Membawa betek dengan bayam","Ada juga yang makan kail","Ta' perchaya tanya sepat Siam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128414,"Inche Uda membeli papan","Hendak membuat dinding rumah-nya","Tandok-nya dua kaki-nya lapan","Kalau demikian ketam nama-nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128415,"Inche Udoh chantek rupawan","Berkacha mata dengan lagak-nya","Bukan bodoh berlaku demikian","Choma menchoba agar berjaya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128416,"Inche Udoh mengerat bengkawan","Ka-tanjong jati hendak di-jualkan","Tidak-kah bodoh duhai tuan","Daun keladi na' di-mandikan?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128417,"Inche Udoh pergi sekolah","Mendengar cherita anak memerang","Tidak-lah bodoh orang sebelah","Memasang pelita di-bulan terang?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128418,"Inche Utar ka-Alor Star","Hendak menziarah sanak saudara-nya","Kalau ular menyusul akar","Akan hilang-kah segala bisa-nya?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128419,"Inche Yahaya menjual kopi","Berkacha mata dengan hebat-nya","Malam ia keluar siang ka-tepi","Itu-lah pelita gerangan-nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128420,"Inchek abu blayar ka daik","Sarat sampan muat klapa","Kalu tuan ada kan baik","Jadi tersimpan bukan siapa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128421,"Inchek Alek mengilir taji","Taji di kilir di batu halus","Tidak kolek jalor pun jadi","Asa bolay menempo arus", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128422,"Inchek Alek mengilir tali","Taji di kilir di batu halus","Tidak kolek jalor pun jadi","Asa bolay menempo arus.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128423,"Inchek ali anak chek kadir","Pakay baju chit antalas","Siang malam dudok berpikir","Budi yang baik blom terbalas", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128424,"Inchek Ali anak Smarang","Pagi hari lilet kapala","Sakit hati mata memandang","Bungah sa-kuntum diatas kapala", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128425,"Inchek Ali belayar ka-Bangka","Pusing kemudi lalu ka-Siam","Heran sekali tidak di sangka","Orang berbudi didalam diam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128426,"Inchek Ali Imam Missigit","Dudok sembayang pagi hari","Betol-lah chantek bulan terebit","Tambah bintang bahru berseri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128427,"Inchek Ali jalan ka-sana","Jalan susor tepi prigi","Akan skali tuan terkena","Jangan besok terkena lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128428,"Inchek ali memasak rempa","Kupas klapa tombong bertumbu","Kutika chari paya berjumpa","Serta lupa dapat bertemu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128429,"Inchek Ali memasang luka","Luka di pasang blakang mati","Budi baik bila kan lupa","Sebagi rambot bersimpol mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128430,"Inchek Ali memasang luka","Luka di pasang blakang mati","Budi baik bila kan lupa","Sebagi rambot bersimpol mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128431,"Inchek ali membli buloh","Anak branak dudok meraot","Deri negri menjadi puloh","Selama lama anyot di laot", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128432,"Inchek ali memburu rusa","Rusa balek di tengah hari","Ini hari besok lusa","Maka tak bolay langkat lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128433,"Inchek Ali menatap pagar","Orang semabok tukang sipat nya","Muda johari baik berkabar","Chindawan mabok mana tempat nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128434,"Inchek ali menjual ikan","Alu alu bertali jarang","Skali skali bolay kabar kan","Kalu slalu membenchi orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128435,"Inchek Ali menyulo sotong","Dayong sampay ke tengah laot","Hidop jangan kita sombong","Banyak boleh jadi tersangkot", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128436,"Inchek Ali mermasang luka","Luka di pasang Blakang Hati","Budi baik bila kan lupa","Sbagi rambot bersimpol mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128437,"Inchek Ali mudek ka hulu","Barang dibli dari Semarang","Mau charik hati yang sunggu","Seratus blom dapat sa-orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128438,"Inchek Ali panglima perang","Pergi berperang dengan senjata","Awan di-langit jikalau terang","Chaya bintang bahrulah nyata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128439,"Inchek ali pasang luka","Dapat sekor ikan tamban","Budi tuan tidak ku lupa","Sperti baju pakay di badan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128440,"Inchek Ali pergi bersolay","Raja Indayu masok kota","Jangan sekali menaggong kaseh","Makan berkua ayer mata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128441,"Inchek Ali pergi ka achay","Raja perbah memasok kota","Jangan skali menanggong kasay","Makan berkua ayer mata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128442,"Inchek ali pergi kramat","Nasik sa-pingan ber-kenduri","Ini skali misti ingat","Misti simpan kemdian hari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (128443,"Inchek ali pergi menebang","Sampan prangi memuat kayu","Bulan matari dapat di timbang","Langit yang tinggi siapa tau", 4, 6);
