INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129338,"Jangkan kesasa tali kangkung na","Senga' takut leq dengan jogang","Te besual ndaraq untung na","Kalah menang pada na doang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129339,"Jangkleq nangke jangkleq poku","Keduaqna tekeleq leq paon","Lamun da poyu bilin oku","Ndaq lupaq ngirim surat bilang aton (Pantun Sasak) (Perpisahan)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129340,"Jangko adat balabeh bari","Lah patuik bamain undang","Kabek arek buhua lah mati","Rumiklah badan dek batenggang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129341,"Jangkuang kayu di rimba","Mandi-mandian orang beranak","Adik membuang abang rela","Sudah kebencian orang banyak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129342,"Jani diengkah di pugu tanju","Pandong dikajang enggau pua kumbu","Belator tuai sida ka indu","Nyugu babi beminyak di tanju (Pantun Sarawak)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129343,"Janieh aienyo Sungai Tanang","Minuman urang Bukik Tinggi","Tuan kanduang tadanga sanang","Baolah tompang badan kami", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129344,"Janji berhibur di Batu Kikir","Kita bersuka di tengah sawah","Jasa ditabur tidak berakhir","Tanah pusaka digembur mewah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129345,"Janji Melayu ingat selalu","Pagi petang kita peliharakan","Terpuji Melayu menghormati tamu","Siapa datang kita muliakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129346,"Janjorak bungo janjari","Kambangnyo sakali tigo","Siriah galak pinang manari","Mandanga tamu nan lah tibo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129347,"Jan-lah padi di-gumpok-kan","Kok gumpok antaro karok","Jan-lah kami di-lupo-kan","Kok lupa antaro lalok.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129348,"Jantan bertina ber-kawan kawan","Ayam s'lasay bulu nya sutra","Tuan laksana pungula awan","Chindra Wasay burong Day'wata.", 4, 6);
