INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129354,"Jaoq-jaoq ojok rantauan","Kepeng te pete jari perombok","Timagne neke tebejoq'an","Lamun wah jodoh gente besopoq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129355,"Japa tumbuh di dekat kundur","Orang dapur membasuh kuali","Lupa hanya waktu tidur","Bangun tidur ingat kembali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129356,"Jarajak di tanah taban","Simantuang di Bukik Putuih","Bakeh batijak nan lah taban","Bakeh bagantuang nan lah putuih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129357,"Jarak nan tumbuh di tepi serambi","Pohon kerekot bunga sama;","Itulah perbuatan yang dibenci Nabi","Petua diikut segala ulama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129358,"Jarak nan tumbuh tepi serambi","Pohon kerekot bunganya sama","Itulah perbuatan yang dibenci nabi","Petuah diikut segala ulama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129359,"Jarak tumbuh di tepi serambi","Pohon kerekut bunganya sama","Inilah hukum dibencikan nabi","Pohon diikut dengan selamat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129360,"Jaran bideng betali rante","Ngaken daun belandingan","Solah raos bagus kerante","Jari indeng sembilinan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129361,"Jaran lepas teriq tipaq telinga","Pelai ojoq tengaq banget dengan","Lueq kanaq kecandu narkoba","Sengaq dengan toaqna kurang Perhatian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129362,"Jaran puteq jaran belang","Mate telanggar isiq montor","Kepeng negare lueq ilang","Sengaq lueq begang kantor", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129363,"Jarang-jarang delima batu","Akanku taruh di atas rumput","Hatiku tarang  saparti laut","Ilmulah Allah  akanku tuntut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129364,"Jarang-jaranglah  dalimo laut","Kan dan lotak di ate rumpuik","Hati dan torang umpamo lawik","Ilmu Allah kan dan tuntuik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129365,"Jarang-jaranglah  dalimo laut","Akanku taruh di atas rumput","Hatiku tarang  saparti laut","Ilmulah Allah  akan kutuntut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129366,"Jarang-jaranglah delima laut","Akanku taruh di atas rumput","Hatiku terang seperti laut","Ilmu Allah akan ku tuntut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129367,"Jarang-jaranglah dindang dalimo laut","Akanku taruh dindang di atas rumput","Hatiku tarang dindang samanya lindang","Ilmulah Allah dindang akanku tuntut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129368,"Jare Landa la kok ireng","Ambon bekne","Jare landa la kok meteng","Balon bekna", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129369,"Jari berdarah tertusuk jarum","Corak daun kipas ditekat","Selagi mewah bijak mencarum","Cukup tahun wajib berzakat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129370,"Jari manusia nendeq girang usil","Agene tedemenang siq dengan","Lamun mele jari dengan berhasil","Harus rajin kanca giat begawean", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129371,"Jari pelayan leq Sinar Bahagia","Mele bekedeq ndeq ne sempat","Girang sombong leq dunia","Tama neraka leq akhirat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129372,"Jari tersusun menyembah duli","Sambil menyembah menundukkan ulu","Awal menyanyi mulalah nyanyi","Apakah lagu penghulu lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129373,"Jariah jo payah nak badaso","Bak bajalan sampai ka bateh","Partamo cukuik alemu di dado","Apo karajo indaklah cameh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129374,"Jari-jari haluih","Seperti budak-budak","Badan tidak kuruih","Oghang cukop segak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129375,"Jari-jari itam","Tak sama jari puteh","Kalau sebelah malam","Oghang pun tak keleh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129376,"Jari-jari puteh","Tak sama jari itam","Kalau oghang puteh","Nampak sebelah malam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129377,"Jaringlah puput merata-rata","Campakkan bilis ke dalam pukat","Keringlah laut menjadi tinta","Takkan tertulis ajaran Muhammad", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129378,"Jarinya lentik seraut wajah","Tuan puteri berbaju belah","Merak intelek menang berhujah","Kertas resolusi taburan sampah?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129379,"Jarinya runcing serai sebatang","Pinggang ramping boleh dicekak","Nasib melukut di tepi gantang","Bagai papan buruk dikopak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129380,"Jarom ber-benang sulam tekat","Serta bertabor bunga batawi","Ibarat menjadi pinang jerkat","Susa berchampor si pinang wangi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129381,"Jarom sepuchot menjait juba","Juba di jait menang mastuli","Di unjong kan tuan dengan bichara","Saya di bawa menjunjong duli", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129382,"Jarum kasar jahit tak sudah","Orang di peran menyulam kain","Tempayan besar sudah kutadah","Malang hujan di tempat lain!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129383,"Jasman roh manusia","Jadi daripada empat anasir","Hendaklah ingat mengeluarkan rahasia","Akhirnya intan menjadi pasir", 4, 6);
