INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129385,"Jasmani sihat menderita roh","Di kelab malam menghabiskan masa","Bergaul bebas lelaki dan perempuan","Dari Aktres ambil terladan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129386,"Jasni Matlani orang kreatif","Banyak menulis cerpen dan puisi","Hidup ini biar produktif","Tidaklah muflis", 4, 6);
