INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129392,"Jatoe ka logok indra-giri","Bli gula satu tempayan","Jikalu panday membawak diri","Banyak orang balas kesian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129393,"Jatoh kalolog indra giri","Buli gula satu tumpayan","Jikalau pandie mumbawak diri","Banyak orang bulas kasihan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129394,"Jatoh berembang di pukol ribot","Jatoh di laman bersatu satu","Kutika ragam misti di ikot","Spaya jangan ber-tengka laku", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129395,"Jatoh bla di kayu lobang","Budak blari kejar menchari","Serta tersandar di kayu chabang","Berbalek kembali tegap berdiri", 4, 6);
