INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129399,"Jatoh patin tertimpa patin","Jatoh jala berlumba lumba","Dalam lahir ada-lah bathin","Dalam bathin berisi  pula", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129400,"Jatuah mumbang jatuah kalapo","Jatuah-lah mumbang niua tinggi","Tuan linjuang kami capo","Dimano bulieh samo tinggi", 4, 6);
