INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129402,"Jatuah mumbang jatuah kalapo","Jatuah bairiang kaduonyo","Rusak adaik hancue pusako","Habislah dayo kaduaonyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129403,"Jatuah mumbang jatuah kalapo","Jatuah bairieng kaduanyo","Rusak adaik hancua pusako","Habihlah dayo kaduonyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129404,"Jatuh bangkit orang berburu","Mengejar kijang ke sana sini","Tubuh ku sakit tulang pun ngilu","Mendengar abang sudah berbini", 4, 6);
