INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129407,"Jatuh berlegar si daun sena","Bunga keranji gugur terlanyak","Kenduri besar ta'ada guna","Puji dan keji sama banyak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129408,"Jatuh berlegar si daun sena","Bunga keranji gugur terlanyak","Kenduri besar tak ada guna","Puji dan keji sama banyak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129409,"Jatuh bertaburan bunga rambutan","Diambil secawan simpan di peti","Tiada hamparan tiada sambutan","Datanglah tuan menabur bakti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129410,"Jatuh bertaburan si biji saga","Pungut pulak ke dalam dulang","Penstrukturan JPP semoga berjaya","Membawa UKM ke puncak kegemilangan", 4, 6);
