INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129412,"Jatuh buah pohon tetangga","Tanah licin Lumpur dipijak","Sahabat baik seperti bunga","Tak bisa terlalu banyak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129413,"Jatuh dari tepi geta","Geta tempat raja semayam","Jangan dibuat cendera mata","Sekadar dibaca di dalam diam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129414,"Jatuh kacang menimpa padi","Pacung talang dihentakkan.","Timbang rasa di dalam hati","Cinta saya tolong pikirkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129415,"Jatuh ke air ditelan bilis","Ditelan bulat dengannya biji","Anak rambutnya melentik wilis","Kening laksana berbentuk taji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129416,"Jatuh ke darat berperai-perai","Di tepi kota Seri Rama","Dunia akhirat tidak hercerai","Abang bercinta sudahlah lama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129417,"Jatuh ke lubuk lalulah mati","Datang berhimpun sekalian ikan","Tetapi tiada dengan seperti","Gemala manikam tuanku kurniakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129418,"Jatuh ke tanah bijinya saja","Disambar merak terabang menari","Siapa tahu menjadi raja","Diarak orang seluruh negeri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129419,"Jatuh ke tanah ditangkap orang","Dibawa pulang berlari-lari","Mana perintah tuan seorang","Kakanda di dalam genggaman jari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129420,"Jatuh ke tanah kayu kuranji","Disambar helang pula bangkainya","Kakanda tiada mengubah janji","Demi dewata dengan benarnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129421,"Jatuh melarah jambu dipungut","Beri tepung kuih dibalas","Keluh-kesah banyak bersungut","Wajah murung semakin jelas", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129422,"Jatuh melayang si daun sukun","Buah kurma dibelah-belah","Anak disayang harta dihimpun","Paling utama kasihkan Allah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129423,"Jatuh pulai menimpa pala","Patah dahan bunga melati","Baju sehelai dipinjam pula","Basah dengan apa ganti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129424,"Jatuh sebiji ke atas karang","Rambunia di atas batu","Tiada siapa boleh melarang","Adat dunia sudah begitu", 4, 6);
