INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129606,"Jeuk sie aneng Pulau Buru","Nangke masak tecampur sie","Lark Malesie ketek beguru","Nengke ite gene ajar Malesie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129607,"Jeuk sie aneng sumur","Laguk endak teme kopresi","Ngumbe Indonesia gene makmur","Lamun pemimpinne buek korupsi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129608,"Jibrail mikail malaekat mulia","Isyarat sifat Jalal dan Jamal","Izrail-israfil rupa pasangannya","I'tibar sifat Qohhar dan Kamal (PANTUN SARABA AMPAT) (Bermuatan Ma'rifat kepada Allah)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129609,"Jibril datang membawa wahyu","Bertempat di tanah Arab","Kepada keluarganya Nabi menyeru","Tapi Abu Lahab tiada berubah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129610,"Jijak selut kaki terbenam","Dipagut ular ekor melilit","Bangkit pagi kuat bersenam","Pecah lender tak jadi sembelit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129611,"Jika ada bunga melati","Mari ku taruk dalam dalam kereta","Ayuh nonalah jantung hati","Ambil keris bertikam kita", 4, 6);
