INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129614,"Jika ada jarum yang patah","Jangan disimpan di dalam peti","Jika ada silap dan salah","Jangan disimpan di dalam hati", 4, 6);
