INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129618,"Jika ada kerbau di kandang","Kasi makan rumput malela","Baik-baik tunangan datang","Tutup pintu buka jendelah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129619,"Jika ada kuda di kandang","Kasi makan rumput melela","Baik-baik tunangan datang","Tutup pintu buka jendela", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129620,"Jika adik masak serunding","Boleh menjamu Kakak Zaharah","Jika cerdik teman berunding","Jika bodoh disuruh arah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129621,"Jika anda pergi ke pekan","Tolong belikan buah embacang","Jika itu yang tuan tanyakan","Tak lain dari air sembahyang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129622,"Jika badik tidak dipinggang","Manakan dapat jadi senjata","Jika adik tidak dipinang","Manakan abang dapat bercinta", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129623,"Jika bata sudah diborong","Dari tukang dari sanak","Jika kata telah terdorong","Minta ampun banyak-banyak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129624,"Jika batang padi yang rebah","Tegakkan tumang diujung tanjung","Jika datang kan kami sembah","Yang tak datang kan kami junjung", 4, 6);
