INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129626,"Jika begini berejong sabut","Hanyut bemban ditunda pasang","Kalo begini ndak dapat hidup","Hilang dendam erahi datang", 4, 6);
