INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129631,"Jika begini rasa padiku","Hanyut cendawan dengan kakinya","Jika begini rasa hatiku","Ayuhai badan", 4, 6);
