INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129636,"Jika benar selasih Jambi","Tanam kerakap tepi permatang","Jika benar kasihkan kami","Kirimkan rokok tiga batang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129637,"Jika berangkat ke Kampung Bisaya","Ambil bersama foto kenangan","Bila mengangkat seni budaya","Akar dan agama dilupakan jangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129638,"Jika berhutang wajib dibayar","Jika meminjam wajib memulangkan","Jika berpegang kepada yang benar","Jika mengidam pada yang siuman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129639,"Jika berkunjung ke Kampung Pengalat","Jalan ke Papar tidak berbukit","Amat kusanjung orang bersolat","Banyak bersabar dukanya sedikit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129640,"Jika berpindah ke Wisma Perkasa","Bukit Bendera dalam pandangan","Jika sudah bergelar dewasa","Sanak saudara dilupakan jangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129641,"Jika dahulu ada musibah","Ucapan duka cita sahabat dan kawan","Jika sekarang mengalami musibah","Istri berkorban menanggung beban", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129642,"Jika dayung tidak kuat","Alamat perahu akan hanyut","Jika kampung tidak beradat","Umat berseteru kehidupan kusut", 4, 6);
