INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129660,"Jika diteropong batang pimping","Tupai melompat malapari","Jika terungkai dengan kelengkeng","Itu bernama biaperi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129661,"Jika gelap orang bertenun","Bukalah tingkap lebar-lebar","Jikalau lenyap tukang pantun","Sunyi senyap bandar yang besar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129662,"Jika gendang tidak berbunyi","Manakan ada muzik peningkah","Jika abang tidak berjanji","Manakan hamba mau menikah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129663,"Jika gendang tidak dipalu","Manakan ada orang bertandak","Jika abang tidak merayu","Manakan hamba mau terjebak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129664,"Jika gendang tidak dipukul","Manakan orang mau menyanyi","Jika abang tidak menyusul","Manakan hamba mau kembali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129665,"Jika halia tiada tumbuh","Ganja Dikarang akan destar","Jika setia tiada sungguh","Bukanlah anak raja besar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129666,"Jika hati suka mendendam","Ke mana pergi banyak seteru","Jika mengerti ajaran Islam","Hidup menjadi telaga ilmu", 4, 6);
