INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129668,"Jika hati suka mendendam","Kemana pergi banyak seteru","Jika mengerti ajaran Islam","Hidup menjadi telaga ilmu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129669,"Jika hendak ke Bangkahulu","Seri Padukalah panglimanya","Jikalau kasih sabarlah dahulu","Adalah masa ketikanya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129670,"Jika hendak tahu pedasnya lada","Makan berkuah berpati santan","Jika hendak tahu di hati saya","Bagai pengarak angin selatan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129671,"Jika hujan hujanlah sungguh","Agar jelas berpayung lilin","Jika enggan engganlah sungguh","Nak tentu mencari yang lain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129672,"Jika ikan tak mau bertelur","Manakan orang dapat memukat","Jika puan tak mau menegur","Manakan abang dapat mendekat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129673,"Jika ikan tidak dijala","Manakan dapat orang membeli","Jika puan tidak menyapa","Manakan dapat abang menanti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129674,"Jika jadi tuan ke pekan","Padi di mana ditugalkan","Jika jadi tuan berjalan","Kami di mana ditinggalkan.", 4, 6);
