INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129676,"Jika jadi tuan ke pekan","Padi di mana ditungalkan","Jika jadi tuan berjalan","Kami di mana ditinggalkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129677,"Jika jala tidak ditebar","Manakan dapat ikan tenggiri","Jika adinda tidak sabar","Manakan dapat abang menanti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129678,"Jika ke kota di Jalan Asmara","Di Bukit Bendera di Jalan Nuri","Tepuk dada tanya selera","Jaga- jaga kesihatan diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129679,"Jika ke Semporna singgah di gua","Gua Gumantong belok ke kiri","Bukan kerana ajaran yang tua","Tapi bergantung pada diri sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129680,"Jika ku jalan sepanjang kali","Sudah ku lihat pulau Melaka","Hilang bini boleh ku cari","Hilang sobat badan binasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129681,"Jika kulurut padiku ini","Kalau kulurut pecah batang","Jika kuturut hatiku ini","Kalau kuturut salah datang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129682,"Jika kutahu pokok tualang","Tidak kubakar menjadi abu","Jika kutahu untungku malang","Tak keluar dari perut ibu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129683,"Jika lengang dalam negeri","Marilah kita pergi ke kota","Hairan tercengang kucing berdiri","Melihat tikus naik kereta", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129684,"Jika liat paku di batang","Kilang diharu dalam tempayan","Jika lambat tuanku laki-laki datang","Hilanglah tahu nujumnya bayan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129685,"Jika liat tanah di Daik","Tabur garam  hingga sebakul","Jika asal niat yang baik","Beri segenggam jadi sepikul", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129686,"Jika mandi sama mandi","Mandi segayung sama tempayan","Jika mati sama mati","Mati selarung sama sekapan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129687,"Jika mandi mandilah kini","Mandi bertimba mangkuk Jawa","Jika mati matilah kini","Gila berhati mabuk saja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129688,"Jika masak pinang setandan","Mari disimpan di dalam kereta","Jika ada tuah di badan","Kaca kupegang jadi permata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129689,"Jika melawat ke Semporna","Jangan tidak ke Pualu Mantadak","Kerja di buat serba tak kena","Ke kanan tidak ke kiri pun tidak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129690,"Jika mendapat si burung banda","Burung pipit jangan dilepaskan.","Kasih tuan genggam-genggam bara","Terasa panas dilepaskan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129691,"Jika menyukat tidak pandai","Ada penuh ada yang tidak","Jika adat tidak dipakai","Ada bergaduh ada bercekak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129692,"Jika menyukat tidak pandai","Ada yang marah ada mengumpat","Jika adat tidak dipakai","Pusaka punah namapun cacat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (129693,"Jika menyukat tidak pandai","Banyak lebih ataupun kurang","Jika adat tidak dipakai","Banyak selisih muka belakang", 4, 6);
