INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130056,"Ka dusun memetek bua","Chari kan saya pepaya ranom","Jikalu ada ilmu pertua","Rachoon yang bisa bolay di minom", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130057,"Ka Guguak marantang tali","Lapeh ka pulau Bukik Gombak","Nan buruak buanglah kami","Cari nan elok lawan galak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130058,"Ke Guguk merentang tali","Sampai ke pulau Bukit Gombak","Yang buruk buanglah kami","Cari yang baik lawan gelak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130059,"Ka hilie jalan ka Padang","Ka mudiek jalan ka Ulakan","Kok musuah indak dihadang","Basuo pantang diilakkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130060,"Ka hilie jalan ka Sumani","Sasimpang jalan ka Singkarak","Saukua mangkonyo jadi","Sasuai mangkonyo rancak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130061,"Ka hilie jalan ke Padang","Ka mudiek jalan ka Ulakan","Kok musuah indak dihadang","Kok basuo pantang dielakkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130062,"Ka hulu menetak pagar","Tetak tetak batang durian","Charik dolu tempat blajar","Spaya jangan menyesal kemudian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130063,"Ka hutan pergi berladang","Ladang di makan burong tekukor","Bila dapat rupa ku pandang","Hati brahi menjadi hibor", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130064,"Ka hutan petek chempedak","Petek dari kebun chek Siti","Sari makan sari tidak","Asal idop jangan lah mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130065,"Ka hutan petek chempedak","Petek oleh kebun Chek siti","Sari makan sari tidak","Asal idop jangan lah mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130066,"Ka kato mambao suluah","Dari munggu tampak barasok","Ibo di badan ka takaciuh","Ilimu jan dibao lalok", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130067,"Ka kebun tanam slaseh","Tanam mari di pokok jati","Jikalau hilang tuan ka kaseh","Di mana saya menchari ganti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130068,"Ka ladang membli terong","Kachang ada chili tidak","Niat hati nak tembak burong","Senapang ada penabor tidak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130069,"Ka lambah mamarang aka","Taparang di buku buluah","Labah basarang kito baka","Ula baguluang kito bunuah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130070,"Ke lembah memarang akar","Terparang akan buku buluh","Lebah bersarang kita bakar","War bergulung kita bunuh (modify?)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130071,"Ka lambah pai mambidiak","Kanailah anak undam-undaman","Tuah tumbuah sumarak naiak","Batuah kampuang jo halaman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130072,"Ka lauik manjalo todak","Buah palo dalam parahu","Pacik padoman kami tidak","Angin bakisa kami tau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130073,"Ka lauik tabang-nyo balam","Ikua kan tidak ta-layok-kan","Ari nan laruik tangah malam","Tidua nan tidak ta lalok-kan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130074,"Ka lubuak jalan ka Nata","Ka ilia Kualo Sokam","Adat taluak timbunan kapa","Adat tuo manahan ragam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130075,"Ke lubuk jalan ke Natal","Ke hilir Kuala Sokam","Adat teluk timbunan kapal","Adat tua menahan ragam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130076,"Ka Makah jalan ka Sariak","Ka Sariak ka Sungai Pua","Nan mano bungo kami ambiak","Batangnyo dipaluik ula", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130077,"Ke Mekah jalan ke Sariak","Ke Sariak ke Sungai Pua","Bunga yang mana kami petik","Batangnya dililit ular", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130078,"Ka mano condong mak nyo rabah","Ka mano tapek mak nyo pai","Sapantun otonom di daerah","Buliah mauruih diri sandiri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130079,"Ka mati juo anyo kacang","Urang-lah payah men-jelo-kan","Ka pai juo malah dagang","Urang-lah payah man-gilo-kan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130080,"Ka Mojopahik sampai barito","Rancak nagari di Pulau Ameh","Gadang mukasuik di dalam dado","Supayo daerah batambah laweh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130081,"Ka Muaro pai manjalo","Banyaklah ikan nan tabawo","Rang mudo nan rambang mato","Namuahlah satean mampakudonyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130082,"Ka Padang mambali lado","Siangik jangan dibandakan","Bacarai lihia jo kapalo","Nan raik jangan dibanakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130083,"Ke Padang membeli lada","Siangik jangan dibendarkan","Bercerai leher dari kepala","Yang raib jangan dibenarkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130084,"Ka Padang manyalang guntiang","Pangguntiang kain kudarang","Campuang-campang lari kuciang","Maliek mancik main padang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130085,"Ke Padang meminjam gunting","Penggunting kain kederang","Tunggang langgang lari kucing","Melihat tikus main pedang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130086,"Ka Padang ka Padang malah","Babatang balimau puruik","Manompang manompang malah","Bapantang babaliak suruik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130087,"Ke Padang ke Padang malah","Berbatang berlimau purut","Mau menumpang menumpanglah","Berpantang berbalik surut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130088,"Ka pakan mambali bada","Dibali baonggok-onggok","Kalau makan samo bajamba","Usah sakali makan basongok", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130089,"Ka pakan mambali ikan","Ikan dibali sarenjengan","Mambasuah tangan sasudah makan","Aia pambasuah nan dituangkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130090,"Ka pakan mambali lado","Dibungkuih jo sapu tangan","Kalau makan jo urang tuo","Usah dahulu basuah tangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130091,"Ka parak usah ka manggih","Kok ka rimbo ka Mangguang sajo","Kok taragak usah manangih","Takiro manuangkan sajo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130092,"Kalau ke parak usah ke manggis","Kalau ke rimba ke Mangguang saja","Kalau rindu usah menangis","Teringat menungkan saja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130093,"Ka parawik lahia jo batin","Ka pangikih miang di kampuang","Miang nak kikih ditabangkan angin","Bungkuak sajangka nak tahambuang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130094,"Ka pasa anak panyalinan","Mambali suto tarang bulan","Bapuluah tahun berurutan","Beranak seorang ka pameran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130095,"Ka pasa balilah sabun","Panyabun kain bugih lamo","Jao tamu ka ka minum","Galeih ak ado ciek juo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130096,"Ka payuang panji di halaman","Panuduangi urang korong kampuang","Sarikat warih mandirikan","Sakato kaum mako dianjuang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130097,"Ka pulau kito ka pulau","Ka pulau manjariang udang","Adaik iduik bagurau-gurau","Kok mati tabariang surang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130098,"Ke pulau kita ke pulau","Ke pulau menjaring udang","Adat hidup bergurau-gurau","Kalau mati terbaring seorang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130099,"Ka pulau kito ka pulau","Ka pulau manjariang udang","Bagurau kito bagurau","Bagurau mangana untuang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130100,"Ke pulau kita ke pulau","Ke pulau menjaring udang","Bergurau kita bergurau","Bergurau mengenang untung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130101,"Ka pulau kito ka pulau","Ka pulau manjarieng udang","Samaso iduik dapek bagurau","Kok alah mati tabarieng surang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130102,"Ka pulo membli ubi","Kayo ka beting langsong-kan jugak","Kalu ingat membalas budi","Lautan api di tempoh jugak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130103,"Ka pulo membli ubi","Kayo ka beting langsong-kan jugak","Kalu ingat membalas budi","Lautan api di tempoh jugak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130104,"Ka puloh mengambil nipa","Buat dinding tiang prigi","Spuloh kasay mak mertua","Mana sama mak sendiri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130105,"Ka puncak barangin-angin","Babelok jalan ka kida","Diagak urang tak ingin","Budi baiek nan dikaja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130106,"Ka rantau madang di hulu","Babuah babungo balun","Marantau buyuang daulu","Di rumah baguno balun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130107,"Ka rimbo baolah api","Takiakkan di pinang-pinang","Takiro ramehlah ati","Katokan dimalah pinang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130108,"Adik mandi kami menyauk","Di sumur orang Koto Tuo","Adik mati kami mengamuk","Sekubur kita berduo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130109,"Ka rimbo bapikek balam","Dipikek jo balam jantan","Kalau kito urang Islam","Sumbayang nan limo wajib dikarajokan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130110,"Ka rimbo pai baraka","Tatupang ka kayu mati","Tuan bacinto dalam ama","Kami bacinto dalam ati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130111,"Ka rimbo ka Padang jangan","Bungo cimpago kambang biru","Tacinto tagamang jangan","Adaik mudo manangguang rindu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130112,"Ke rimba ke Padang jangan","Bunga cempaka kembang biru","Bercinta teggamang jangan","Adat muda menanggung rindu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130113,"Ka sawa orang berlapan","Jumpa kabong mayang di tenga","Banyak bua suda di makan","Mabok jagong belom perna", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130114,"Ka sawa orang berlapan","Jumpa kabong mayang di tenga","Banyak bua suda di makan","Mabok jagong blom perna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130115,"Ka subarang batuduang pinggan","Panuah barisi layu-layu","Kasiah taulan tainggan-ingan","Kato kami tadoroang lalu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130116,"Ka suok jalan ka Supinang","Ka kida jalan ka Muaro","Jo suok jawek kasieh sayang","Jo kida apuih aie mato", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130117,"Ka suyuak bilah rang lantai-kan","Ambiek-lah rotan ka pa-ngarang","Saba je rila di-pakai-kan","Ba-itu urang samo gadang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130118,"Ka tanah lakang balalukan","Utang dibayia piutang ditarimokan","Babatulan babayaran bahukum bapatutan","Piutang ditagiah manariak mangumbalikan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130119,"Ka tangah padang maracak kudo","Digatiah mangko dilacuik","Rang gadang tahu di gadangnyo","Nan ketek mati tasapik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130120,"Ka teluk sudah ka tanjong sudah","Ka Mekah sahaja yang belum lagi","Ku pelok sudah ku cium sudah","Nikah sahaja yang belum lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130121,"Ka tiga ang di negri kling","Berjumpa kin ada di situ","Penchana banyak diri kuliling","Baru lah chinta datang berbantu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130122,"Kaak kacang","kuda kandis","Masak masam","muda manis.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130123,"Ka-alor star menjual parang","Besi kharsani berbagai rupa","Kalau terlantar di-rantau orang","Ikut-lah resmi si-ayam betina", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130124,"Kaampek banamo 'gantuang kamudi'","Samacam cukai palabuahan","Kappa parahu nan lah bagantuang kamudi","Kapado rajo cukai bayiakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130125,"Kaampek banamo samun saka","Manyamun urang jo kakarasan","Harato diambiak basabuang jiwa","Karobannyo mati atau pun pingsan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130126,"Kaampek cadiak cando ka iyo","Bungkuih elok pangabek kurang","Kumari tangguang sagalo caro","Ka bajak unjua ka singka ampang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130127,"Kaampek hiduik di langik","Iman taguah amalan taat","Tahu di mungkin dangan patuik","Dunia akirat nak salamat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130128,"Kaampek kato kamudian","Patuik bana kito mancari","Taruah naraco jo katian","Paniliak langgam nan tadiri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130129,"Kaampek Simawang jo Bukik Kanduang","Pardamaian Koto Piliang","Di sinan tampek urang bakampuang","Sangketo damai salisiah hilang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130130,"Kaampek undang-undang nan duo puluah","Ta aluak kapado rupo kasalahan","Anam cemo anam tatuduah","Ditambah undang-undang nan salapan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130131,"Kaampek urang nan suko mahindakan doso","Tak namuah bajalan di tapi tabiang","Jalan ditampuah jalan agamo","Bapantang bana ka manyaleweng", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130132,"Kaanam banamo rabuik rampeh","Dahulu marabuik pado urang","Barang tapacik ambiak jo kareh","Lalu dirampok hati tak gamang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130133,"Kaanam cadiak nan tiok sasuatu","Banyak alemu di dalam dado","Dunia akirat sagalo tahu","Elok jo buruak bateh tiado", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130134,"Kaanam Datuak Rajo Endah Sulit Ayia Tanjuang Balik","Cumati Koto Piliang","Hukum tak jalan nan tasulik","Sampik paralu mampalapang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130135,"Kaba sadiah jokok tadanga","Hati manarimo handaklah saba","Kaba suko pulo tadanga","Syukur ka Tuhan jo hati rela", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130136,"Ka-bagan datok membawa pelam","Singgah memanching di-kuala gula","Buaya batok hamba ta' demam","Hanya kuching pening kepala", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130137,"Kabaitullah di tanah suci","Datang ke sian minta selamat","Tuhan Allah yang maha suci","Jangan dilupakan setiap saat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130138,"Kabar berita pasirnya tumboh","Tempat urang mengambik kerang","Sukor rahsia disimpan tegoh","Mentara belum ditauk urang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130139,"Kabasaran padang nan bacabuik","Langkok jo gandang pararakan","Raso kalu lidah manyabuik","Bana tak buliah diilakkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130140,"Kabau baduo susukan anak","Urang gubalo kalaparan","Bogo mancampua ka nan banyak","Suaro tidak kadangaran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130141,"Kabau bahimpun tangah padang","Nan bancah jadikan sawah","Jokoknyo biyang kucing jalang","Laku saroman jo mantirah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130142,"Kabau batuntun dari Aceh","Singgah manyasok di muaro","Siriah batungkuih jo karateh","Pinang bagatok jo suaro", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130143,"Kabau den nan panjang tanduak","Nak tali sambilan tali","Adiak den nan panjang abuak","Nak minyak di tangan kami", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130144,"Kerbauku nan panjang tanduk","Ingin tali sembilan tali","Adikku yang panjang rambut","Ingin minyak di tangan kami", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130145,"Kabau gadang di ujuang tanjuang","Tanduaknyo malingka bumi","Gadanglah doso adiak tangguang","Kami imbau indak babuni", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130146,"Kerbau besar di ujung tanjung","Tanduknya melingkari bumi","Besarlah dosa adik tanggung","Kami imbau tidak berbunyi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130147,"Kabau gadang lari manduo","Tiok dipanah tiok kanai","Kampuang dibari ba rang tuo","Rumah dibari batungganai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130148,"Kabau gadang Sibinuang","Singgah manyasok ka jarami","Kaco pacah minyak tatunggang","Sinan bagaluik ujuang jari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130149,"Kerbau besar Sibinuang","Singgah menyesap ke jerami","Kaca pecah minyak tertunggang","Di situ bergelut ujung jari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130150,"Kabau nan di dalam parik","Makan barulang ka limpato","Kasiah bamulai dalam raik","Tagah di adiak tidak nyato", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130151,"Kabau nan gadang disangko induak","Cawang nan runciang mancucuak-cucuak","Paruik tabosai jangek lah tabuak","Lari kalau mati bakaluak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130152,"Kabau nan gadang ka bancan","Tunjang siapo ka mambali","Adiak dimukasuik ka pakaian","Ilang siapo ka mangganti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130153,"Kabau nan julai kandang parik","Makan barulang ka limpato","Kasiah bamulai dalam raik","Tagah deh adiak balun nyato", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130154,"Kerbau terjulai kandangnya parit","Makan berulang-ulang ke limpato","Kasih bermula dalam raib","Sayangnya bagi adik belum nyata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130155,"Kabau putiah makan salibu","Makan salibu padi sawah","Den piliah dalam saribu","Ajo surang den pandang sudah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130156,"Kerbau putih makan salibu","Makan salibu padi sawah","Saya pilih dalam seribu","Abang seorang terpandang sudah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130157,"Kabau-nyo banyak tangah padang","Banyak sa-ratuih duo-puluah","Patuik ka jodoh urang gadang","Asaleh ati samo sungguah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130158,"Ka-bukit tengah di-waktu pagi","Bawa bersama batang selaseh","Siang ka-tengah malam ka-tepi","Itu nama-nya si-tepak sireh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130159,"Kabun Situkuak di Tantaman","Jalan ka Danau sadionyo","Kok amuah adiak baganggaman","Kami marando sadionyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130160,"Kebun Situkuk di Tantaman","Jalan ke danau rencananya","Kalau mau adik bergenggaman","Kami menduda rencananya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130161,"Kabung enau tebang satu","Tebang sekali dengan sigainya","Tinggi gunung tinggi lagi harapanku","Harapan dalam tutur katanya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130162,"Kabus pagi menutup awan","Hari cerah menjadi redup","Kuikut janji menanti tuan","Hati resah jantung berdegup", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130163,"Kaca baru diberi berbingkai","Ambil peti muatkan panah","Daripada hidup becerminkan bangkai","Baik mati berkalang tanah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130164,"Kaca baru diberi bingkai","Ambil peti muatkan panah","Pada hidup bercermin bangkai","Baiklah mati berkalang tanah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130165,"Kaca batu beri berbingkai","Ambil peti muatkan panah","Pada hidup bercermin bangkai","Baiklah mati berkalang tanah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130166,"Kaca batu beri bertangkai","Ambil peti muatkan panah","Pada hidup bercerminkan bangkai","Baik mati berkalang tanah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130167,"Kaca bertutup ikannya kalui","Bingkai menarik elok berbumbung","Mercun meletup Cik Merduwati oi","Buat mengusik perempuan yang sombong", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130168,"Kaca biru buatan China","Dirapat dengan batu Belanda","Sangat merayu dagang yang hina","Hati di dalam sangat menggoda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130169,"Kaca biru buatan China","Dirapat dengan batu Belanda","Sangat merayu dagang  yang hina","Hati di dalam sangat menggoda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130170,"Kaca biru buatan Cina","Dirapat dengan batu Belanda","Sangat merayu dagang yang hina","Hati di dalam sangat menggoda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130171,"Kaca di ambil bekasnya cuka","Anak ayam putih sembilan","Teguh kasih membawa suka","Suka membawa badan kesian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130172,"Kaca dipalu pecah retak","Dilipat kain digulungkan","Mata bertemu badan tidak","Makrifat saja menanggungkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130173,"Kaca dipalu pecah retak","Dilipat kain digulungkan","Mata bertemu badan tidak","Makrifat saja menanggungkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130174,"Kaca jatuh pecah berderai","Ribut bunyinya dalam ruangan","Buruk muka jangan dinilai","Urusan rumah jadi pegangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130175,"Kaca kabur jendela rumah","Dindingnya berwarna suram","Jangan tekebur menuduh orang","Kalau fakta bukan jaminan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130176,"Kacamata tebal lensanya","Pandang ke padang jauh berkabut","Raba kantong pingin belanja","Kantong kosong hutang bertumpuk", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130177,"Kacang bendi buah mentimun","Sayur dagang dari Kundasang","Hilang budi akal disamun","Gelap pandangan walaupun siang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130178,"Kacang botor diratah-ratah","Sedap juga kalau dikukus","Direndam semangat waja tak basah","Dibakar memang tak hangus!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130179,"Kacang bukan sebarang kacang","Kacang kedelai dari Periaman;","Datang bukan sebarang datang","Hendak menyunting bunga di taman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130180,"Kacang buncis kacang panjang","Makan bersama si buah hati","Apa guna berpedang panjang","Kalau tidak berani mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130181,"Kacang hantu di ataih bukit","Kacang panjang di ataih tambak","Kalau dulu buaih sakit","Masa sekarang dah baek banyak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130182,"Kacang hijau diberi gula","Sudah masak jadi penganan","Abang risau hati menggila","Dah lama agak menanti tuan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130183,"Kacang hijau dibuat inti","Pisang salai dalam perahu","Bunga di taman masih sunti","Mengaji tak pandai ke dapur tak tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130184,"Kacang hijau didalam talam","Dibuat penganan lazat sekali","Abang mengigau siang dan malam","Mengingat tuan tambatan hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130185,"Kacang hijau ditumbuk halus","Putu kacang diterap-terap","Kereta laju lebuh raya lurus","Jalan lebar baru diturap", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130186,"Kacang hijau kuih kasturi","Di pasar tani boleh dibeli","Keluarga bahagia bertambah seri","Isteri sejati ibu mithali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130187,"Kacang kecambah di dalam perahu","Puan dibeli di Serati","Bertukar tambah abang tak mau","Tuan juga belaku mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130188,"Kacang kedelai dari Periaman","Langsat dibawa ke Brastagi","Harapkan apa bunga di taman?","Kuntum belum mengembang lagi! (Orang Meminang)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130189,"Kacang kuda si kacang parang","Aer manis di dalam cawan","Banyak pemuda jeman sekarang","Mulut manis lidah bercabang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130190,"Kacang panjang belimbing sagi","Di potong-potong buat kerabu","Mata panjang bergerigi-gerigi","Boleh dibuat pemotong kayu (Gergaji)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130191,"Kacang panjang melilit batang","Melilit dibatang memanjat pinang","Bukan datang sembarang datang","Datang baik ingin meminang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130192,"Kacang panjang melilit gelegar","Labu menjalar di tepi perigi","Nasib si bujang banyak bersabar","Tunang disambar menggigit jari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130193,"Kacang paringek dalam parak","Dikarek pandan jo durinyo","Awa diingek akie indak","Alamaik badan ka binaso", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130194,"Kacang paringek tapi aia","Lah mati mangko babuah","Ingek-ingek pelang balayia","Lauik sati rantau batuah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130195,"Kacang paringek di tepi air","Kalau mati baru berbuah","Ingat-ingatlah pelang berlayar","Lautan sakti rantau bertuah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130196,"Kacang sepat tumbuh serumpun","Tanam ubi di tepi paya","Budi hanya setitik embun","Gugur ke bumi hilangkan ia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130197,"Kacang siapo koh lah iko","Jelo ba jelo di pagar-an","Dagang siapa kohl ah iko","Ba ibo-ibo ka ba jalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130198,"Kacau dodol gula merah","Hendak pekat campurkan kanji","Gunung gondola bukit ditarah","Lalu apa yang tinggal lagi?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130199,"Kachik kachik batang ber-duri","Kayet kayet mangayet paku","Dari ku suka sampay ku benchi","Krana payhil tengka laku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130200,"Kacindorongan mato rang banyak","Barubah sajo tibo-tibo","Urang kailangan batuka lagak","Di sinan tumbuah diri tacemo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130201,"Kacip ini sudah berkarat","Manakan telap membelah pinang","Nasibku ini sungguh melarat","Kekasih lesap dibawa orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130202,"Kacip pinang berkati-kati","Sirih bersusun di dalam puan","Sebab muda punya kendati","Kerana kekasih jangan di buang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130203,"Kacip pinang kacip tembaga","Kacip ada di atas jerami","Tali panjang tersimpul juga","Puncanya ada di tangan kami", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130204,"Ka-dalam dulang isikan chawan","Minum bersama dara jelita","Sunggoh malang nasibmu tuan","Terharu juga hati hamba", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130205,"Kadang-kadang ia berehat","Naik kereta ke Melaka","Supaya kehidupan tambah sihat","Harus terhindar hati yang duka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130206,"Kadu ngumbel lemari kaca","Lamun ndeq wah berisi tangkong","Kadu ngumbe maen cinta","Lamun kance dedare Hongkong", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130207,"Kaduo ba alemu di dalam dado","Tahu di adat jo limbago","Ka pai tampek batanyo","Ka pulang bakeh babarito", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130208,"Kaduo banamo 'pangaluaran'","Mambayia cukai kapado rajo","Sagalo barang parniagaan","Dari rantau dikaluakanyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130209,"Kaduo banamo tikam bunuah","Manikam luko atau mati","Kalau mati hukumnyo bunuah","Darah taserak bangkai lah lali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130210,"Kaduo cadiak kaji mangaji","Bakeh nan binguang balanteh angan","Kapalo tatakua muluik takunci","Jo urang pandai kalau baadok-an", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130211,"Kaduo hiduik dilauik","Sampan pangayuah kagadangan","Alat langkok pakakeh cukuik","Namonyo tagak di patukangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130212,"Kaduo kuek mangarajokan ibadat","Kawajiban baagamo ditunaikannyo","Manyambah Allah sangatlah taat","Basurang-surang atau basamo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130213,"Kaduo limbago kato mupakat","Sakato urang nan basamo","Elok sapaham sahakikat","Santoso kito balamo-lamo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130214,"Kaduo tamasuak di nan lahia","Manahani berang jo bangih","Urang pamberang kurang bapikia","Napasu dikandalikan setan ibilih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130215,"Kaen serasa lipat baginie","Kaen kaen basa djemoer die talie","Apa rasa idoep begienie","Lebie baik matie sekalie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130216,"Ka-gunong inas menchari buah","Buah keriang manis rasa-nya","Badan di-atas nyawa di-bawab","Itu-lah layang-layang nama-nya ia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130217,"Kahain talupok negri kling","Pakian anak datok penghulu","Ombak batubok akar basabing","Manti kan ujan dari ulu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130218,"Kahie ajie ada mahoe die gomie","Na kaboene nahoe kaboenoe","Tjielaka andango","Da makaoe ta andienga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130219,"Kahie tjie ade nahoe die gomie","Moe trie marime die ada nahoe maramo","Ambeko kento","Da loa ba nahoe", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130220,"Kahit kahit pagar kan bunga","Tuak magang di rantoh panjang","Baik jahat saya trima","Butua badan umor panjang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130221,"Kaibatullah tempat bertawab","Mengitari ka'bah cukuplah panjang","Sebelum maksud nak kami jawab","Yang dilamarkan ini duda atau bujang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130222,"Kaibatullah tempat bertawab","Ummat Islam banyak yang datang","Sebelum maksud nak kami jawab","Yang dilamarkan ini duda atau bujang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130223,"Kaih pagi makan pagi","Kaih petang makan petang","Buat kerja laki bini","Nak bela anak soghang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130224,"Kaih pagi makan pagi","Kaih petang makan petang","Kjalau tak kerja sehari","Tak boleh beli barang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130225,"Kaik bakaik rotan sago","Takaik di aka baha","Sampai ka langik tabarito","Tibo di bumi jadi kaba", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130226,"Kaik kalawi tinggi-tinggi","Jatuah ka tanah pacah balah","Kalau kan lai kini-kini","Nyawo di dalam tangan Allah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130227,"Juluk kelawi tinggi-tinggi","Jatuh ke tanah pecah dan belah","Kalau mau kini-kini","Nyawa di dalam tangan Allah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130228,"Kaik-bakaik rotan sago","Di baliak batang pauah janggi","Menurut adat nan biaso","Barila juo ka banyanyi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130229,"Kail diumpan dengan belalang","Belalang rusa ditangkap petang","Menekan menghimpit rakan yang malang","Jasa yang lepas haram dikenang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130230,"Kail ini panjang sejengkal","Hendak memancing mana umpannya","Hujan badai dapat kutangkal","Hati kasih apa obatnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130231,"Kail mayong di pantai landai","Dapat seekor dilapah dilayat","Beruntunglah orang yang pandai","Mencari ilmu sepanjang hayat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130232,"Kail sejengkal mengail di jeram","Pasang taut di pinggir lurah","Bermotosikal berlumba haram","Kalau tak maut cedera parah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130233,"Kain antelas dari utara","Pakaian anak raja Belanda","Bahtera emas layarnya sutera","Hendak belayar angin tiada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130234,"Kain baju di dalam rombong","Rombong merah kelarai buluh","Ingin maju ilmu bersambung","Tumpul diasah gelap bersuluh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130235,"Kain baju sutera kembang","Letak berlipat dalam almari","Lain dituju lain yang tumbang","Tidaklah dapat idaman hati", 4, 6);
