INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130512,"Kain putih kain kelambu","Jela berjela di atas pagar","Betapa kumbang tak akan tahu","Bunga telah kembang tiap dahan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130513,"Kain putih koyak di tengah","Pakaian anak Dato' penghulu","Sedang kasih dapat fitnah","Biar saya berundur dahulu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130514,"Kain putih luntur disesah","Pakei perecak mengelap talam","Mengenang kekasih resah gelisah","Bagei berkocak seluruh alam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130515,"Kain putih lusuh diulit","Terulit pulang kayu teranak","Kasih orang di luar kulit","Inda menyama kasihnya anak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130516,"Kain putih panjang sahasta","Gunting chukop satu jangkal","Tulebih dulu suda preksa","Saya tidak jadi munyasal", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130517,"Kain putih panjang sembilan","Sepuluh dengan rambu-rambunya","Encik putih tidur di tilan","Titik peluh tinggal baunya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130518,"Kain putih panjang sembilan","Sepuluh dengan rambu-rambunya","Encik Putih tidur di tilam","Titik peluh tinggal baunya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130519,"Kain putih pucha di tanga","Pakie anak datok pangulu","Kalu bukasih jadi tak lama","Baik la saya hundor dulu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130520,"Kain putih sujiannya putih","Belum habis jarumnya hilang","Jika dipandang panau yang putih","Tiadalah sedar nyawaku hilang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130521,"Kain sakhelat baru dibeli","Dibeli nak orang Banda Dalam","Taulan terikat dalam hati","Jadi mimpi siang malam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130522,"Kain samping tergores","Terletak di kincir gila","Anak si anu nan halus","Duduk di pintu menggila", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130523,"Kain sandusi istim berakim","Turunan di pintu syurga","Pertama sabar kedua yakin","Rezeki datang mengaga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130524,"Kain sangkulat plangey kuda","Pakien anak raja di awan","Dalam ibarat chinta kakanda","Rasa nak mati di pangku tuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130525,"Kain sarung berujung empat","Tertutup kain celana","Tak ada keadilan suatu tempat","Ancaman di mana-mana", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130526,"Kain sarung di dalam puan","Kain berlipat di atas peti","Tidakkah tuan iba kasihan","Melihat kekanda makan hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130527,"Kain satin bagus di sulam","Sulaman indah mengangkat darjah","Perempuan pertama beriman dan Islam","Istri Nabi sendiri Siti Khadijah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130528,"Kain satin berwarna-warni","Indah bermutu tulen sejati","Dato' dan Datin giliran kini","Anak menantu sedia menanti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130529,"Kain satin pakaian Anum","Corak berbaris pakai beradu","Senyum pengantin semakin ranun","Semoga manis di lautan madu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130530,"Kain satin tempak bersumbi","Ambil benang lalu di sulam","Ajaran utama di sampaikan Nabi","Hendaklah beriman di dalam Islam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130531,"Kain satin warnanya redup","Bunga bercambah si rama-rama","Selamat pengantin menempuh hidup","Kekal muhibah selama-lama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130532,"Kain saya kain basahan","Kain buruk dipakai jangan","Pantun saya pantun basahan","Didengar boleh dipakai jangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130533,"Kain segebar dua gebar","Pakaian anak raja Maulana","Sauh tercampak kakar terjajar","Jung bergerak pulau bertanya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130534,"Kain segebar dua gebar","Pakaian anak Raja Maulana","Sauh tercampak karang terjajar","Jong bergerak pulau bertanya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130535,"Kain selendang dasarnya putih","Habis dijemur direndam basah","Adik jangan diajar kasih","Habis umur dendam tak sudah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130536,"Kain selendang tenunan Kampar","Putus sehelai atas kasur","Diulas dengan empu tangan","Dibungkus dengan kain kasap", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130537,"Kain selerang kain pelekat","Payung terkembang tengah hari","Sungguhlah abang pandai memikat","Burung terbang menyerah diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130538,"Kain selimut dibawa mandi","Kain basahan dibawa pulang","Ibadat dan ilmu dibawa mati","Mas dan perak tinggal di orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130539,"Kain selindang hisis di tabuk","Kemunting mari luruh bunganya","Kapal berlenggang kelasi mabuk","Jurumudi akan ke mana", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130540,"Kain selindang hisis di tabuk","Matahari masuk memanah","Kapal berlinggang kelasi mabuk","Panduan tidak lagi teranah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130541,"Kain selindang kesumba Murut","Mari diikat si rotan saga","Hati berahi jangan diturut","Biar ditantang jangan diaga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130542,"Kain seperai ragikan nila","Seperai Sitti dari Bayang","Bercerai main lagi menggila","Konon bercerai kasih sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130543,"Kain seperai ragikan nila","Seperai Siti dari Bayang","Bercerai main lagikan gila","Konon bercerai kasih sayang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130544,"Kain seperti bunga dedap","Dibawak dari negeri Keling","Darah gemuruh jadi tak tetap","Kepada cinta tidak berpaling", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130545,"Kain seperti bunga derdap","Pohon mempelam di tepi huma;","Darah gemuruh jadi tak tetap","Tidur malam haram tak lena.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130546,"Kain seperti bunga derdap","Pakaian anak Raja Yaman;","Darah gemuruh jadi tak tetap","Merosak hati merebahkan iman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130547,"Kain serasa lipat bagini","Kain basah jemur bertali","Apa rasa hidup begini","Lebih baik mati sekali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130548,"Kain serupa bunga dedap","Dibawa dari negeri Keling","Darah gemuruh jadi tak tetap","Cinta akan tuan tidak berpaling.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130549,"Kain setra tenunan kayang","Kalu kayang ada puncha nya","Tidak begitu orang sayang","Kalu sayang ada rupa nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130550,"Kain Siam puteh melepak","Pakian anak Rajah Bugis","Kalu diam kan kuasa ombak","Tentu daratan menjadi habis", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130551,"Kain sikalat baru dibali","Dibali nak urang Banda Dalam","Taulan taikek dalam ati","Jadi mimpi siang malam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130552,"Kain singklat di atas peti","Rumpot wangi tengah jalan","Tidor sa-lerlap masok nimpi","Bayang bayang sangka kan tuan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130553,"Kain sisampiang bagaruih","Talatak di kincia gilo","Anak si anu nan aluih","Duduak di pintu manggilo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130554,"Kain songket berbenang emas","Ditenun orang di Kota Baharu","Jangan kita selalu cemas","Supaya hidup tidak keliru", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130555,"Kain songket di atas peti","Rumput wangi tengah jalan","Tidor sa-lelap masok mimpi","Bayang bayang di sangka tuan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130556,"Kain songket dimakan gegat","Baju pengantin tuan puteri","Tiada nasi ubi disagat","Kena lauknya menjilat jari!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130557,"Kain songket panjang sembilan","Panjang serta kapala nya","Tuan sempama permata intan","Saya menumpang akan chaya nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130558,"Kain songket tenun Melayu","Berasai dari tanah seberang","Hatiku sakit pandang ke susu","Susu kecik tak dapat pegang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130559,"Kain songkit biru laut","Di-sulam pula benang sutera","Asam di-bukit ikan di-laut","Sampai ketika bersua juga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130560,"Kain songkit warna-nya merah","Pakai berjalan ka-negeri kedah","Salah sadikit abang na' marah","Chari jalan hendak bergodah-kah?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130561,"Kain suji di atas atap","Pakaian raja hari raya","Jika sudi tuanku santap","Jika tiada apakan daya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130562,"Kain suji diatas atap","Belum sudah baharu dilipat","Kalau sudi silah-lah santap","Sireh kering pinangnya kelat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130563,"Kain sutera berkabung-kabung","Pakaian anak raja maulana","Pasang jerat dihujung tanjung","Mengkarung lalu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130564,"Kain sutera berkabung-kabung","Pakaian anak Raja Maulana","Pasang jerat di hujung tanjung","Mengkarung lalu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130565,"Kain sutera di pakai nyaman","Untuk penutup atas kepala","Setiap muslim harus beriman","Kepada Yang Satu Allah Ta'ala", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130566,"Kain sutera kain muri","Kalau kertas di tenun jangan","Main kita main mencuri","Kalau terjumpa tersenyum jangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130567,"Kain suto labah mangirok","Dibawo urang mudiak Padang","Tampak ajo darah samirok","Badan bak raso bayang-bayang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130568,"Kain sutera lebah mengirap","Dibawa orang mudik Padang","Tampak abang darah semarak","Badan serasa bayang-bayang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130569,"Kain sutra baju nya sulam","Bunga padi di dalam selaseh","Terkejut jugak di tengah malam","Hati ku menagis teringat kaseh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130570,"Kain sutra dari Palembang","Gunting di-badan lalu paysak","Ibarat sa-umpama burong terbang","Nantikan musim padi masak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130571,"Kain sutra ternun di Achay","Kalu di Achay  ada puncha nya","Sunggu lah tuan hati yang kasay","Kalu ber-kasay apa rupa nya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130572,"Kain talupok negri kling","Pakayan anak datok pengulu","Ombak ber-tolak akar basabing","Nanti kan hujan deri ulu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130573,"Kain tanjung gandam berlari","Tenunan mari anak Barunai","Tuan dendam tuan ke mari","Apa jauhnya melangkah sungai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130574,"Kain telepok deri Kling","Pakay anak Datok pengulu","Ombak bertepok di tepi tebing","Nanti kan hujan deri hulu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130575,"Kain Telepok tekat bersuji","Kasi pakay hulu balang","Kubu nya tembok belapis besi","Hantam peluru habis terbang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130576,"Kain telepuk berhias kencana","Perada terbang nipis melayang","Acuan kayu berukir bunga","Seni Melayu zaman gemilang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130577,"Kain telepuk perada emas","Pakaian menteri dalam puri","Semangat dibentuk akal nan bernas","Puteri puteri pembela negeri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130578,"Kain telepuk perada emas","Perada emus bunga melor","Janji setia diikat kemas","Cinta sejati hati nan luhur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130579,"Kain telepuk tidak berkelim","Sudah dipakai Cuma sekali","Bagai pokok di empat musim","Kasih berganti jodoh kembali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130580,"Kain tersangkut jatuh ke lumpur","Jatuh dari tepi geta","Dapat sedikit seribu syukur","Jangan dibuat cendera mata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130581,"Kain tetas belum berjahit","Menanti jarum dari Belanda","Hatiku balas belum tah baik","Menanti ubat dari adinda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130582,"Kain tongkah di atas peti","Cerah warna nampak terserlah","Cara apakah mendapat simpati","Dari manusia serta Allah?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130583,"Kain tudong tekat berawan","Di hempet songket bunga bercharang","Terbang burong lebat sa-kawan","Saya pipit menumpang sorang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130584,"Kain tudung kain pelangi","Kain dibeli di kedai Cina","Sungguh merdu adik menyanyi","Abang teringat tidur tak lena", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130585,"Kaingaran mula ditebas","Tempak laksamana memburis ajung","Orang lama sudah dilepas","Orang baharu lagi ditanggung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130586,"Kaingaran mula ditebas","Tempat orang memburis jagung","Dendam lama sajakan lepas","Dendam baharu pula ditanggung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130587,"Kain-kumbalan kamba banang","Pakai-an anak Rajo Ali","Sa-balun dapek balun sanang","Antah kok jiwa tidak lai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130588,"Kainlah putih disembunyikan","Kain pelekat dikampuhkan","Orang nan mati jangan dihibakan","Oranglah balik ke kampungnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130589,"Kainlah putih terlihit-lihit","Terlihit-lihit di dalam nibung","Kasih orang di luar kulit","Baiklah kasih kepada indung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130590,"Kainlah putilah disembunyikan","Kain palikat dikampukan","Orang nan mati jangan difikirkan","Orang nan pulang ke kampungnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130591,"Kainlah putio disambunyikan","Kain palekat dikampuhkan","Urang nan mati jan diibokan","Urang baliok ka kampuongnyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130592,"Kainnya lebar putih bersih","Cat biru batik dilukis","Hati terdampar di pantai kasih","Ombak rindu cinta terhakis", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130593,"Kait berkait rotan saga","Sudah terkait di akar bahar","Terbang ke langit terberita","Tiba di bumi jadi kabar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130594,"Kait buah cermai","Cicah garam belacan","Adik buat perangai","Kaka tidak makan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130595,"Kait buah nipah","Pokok di tepi sungai","Sakit pakat renggah","Pi berubat buat perangai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130596,"Kait daun layur di para","Buat tudung kuih talam;","Pahit maung sama dirasa","Sama diharung timbul tenggelam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130597,"Kait kait batang kundor","Ka-tiga dengan batang bidara","Bukan kah baik kita hundor","Asal jangan jadi perkara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130598,"Kait kait batang umpalas","Sekat pagar tana sebrang","Timbangan baik jikalu jelas","Senang meniaga di negri orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130599,"Kait kait di atas rakit","Rakit di pusing masok kuala","Entah baik entah ka sakit","Sakit pening tak siapa bla", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (130600,"Kait kait di tepi prigi","Lalu mengamek timba nibong","Ilang raip kamana pergi","Habis bulan taun menyambong", 4, 6);
