INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131422,"Kalau menangkap burung punai","Jangan dibawa tajur biawak","Kalau bercakap pada nan ramai","Jangan dinampakkan bodoh awak", 4, 6);
