INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131424,"Kalau menapi berbanjar-banjar","Kalau mendekut bergalah-galah","Kalau berani pada yang benar","Kalau takut pada yang salah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131425,"Kalau mencangkul dibulak balik","Tanahnya lunak tanaman subur","Kalau setukul adik beradik","Tuahnya nampak kehidupan makmur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131426,"Kalau mencari periuk belanga","Dekat tempayan di tepi karung","Senyum isteri sampai telinga","Seguni pakaian depa borong", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131427,"Kalau mencolup asau bengkuang","Pantang dikoat di pangkal tanggo","Kalau idup dik di antau uang","Poganglah adat dengan lembago", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131428,"Kalau mendapat sama berlaba","Bila merugi sama hilang","Kalau taat memegang agama","Hidup mati takkan terbuang", 4, 6);
