INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131430,"Kalau menebang kayu nan tinggi","Ingat-ingat rantingnya patah","Kalau terbang nak tinggi-tinggi","Ingat-ingat nak bumi di bawah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131431,"Kalau menebang kelapa muda","Jangan tebang pokok lain","Kalau abang kasehkan dinda","Jangan paling pada yang lain", 4, 6);
