INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131433,"Kalau menebang sama mengerat","Kalau membakar sama menyuluh","Kalau menimbang sama berat","Kalau menakar samalah penuh", 4, 6);
