INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131438,"Kalau mengail di lubuk dangkal","Dapat ikan penuh seraga","Kalau kali panjang sejengkal","Jangan laut hendak diduga", 4, 6);
