INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131460,"Kalau menutup tidak rapat","Bila miring isi tertumpah","Kalau hidup tidak beradat","Bila berunding pasti menyalah", 4, 6);
