INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131472,"Kalau menyurat di kertas biru","Kirimkan surat Datuk Panglima","Saya nak naik ke langit biru","Sanggupkah puan ikut bersama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131473,"Kalau meraut batang buluh","Siapkan lidi buangkan miangnya","Kalau jemput orang nan jauh","Siapkan nasi dengan hidangnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131474,"Kalau merenda supaya indah","Tekat tenun beri berjambul","Kalau meminta kepada Allah","Lambat laun pasti terkabul", 4, 6);
