INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131476,"Kalau merendang buah nangka","Biji direndang rasanya sedap","Kalau dikenang nasib celaka","Hari yang terang terasa gelap", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131477,"Kalau meruduk di kayu putat","Banyaklah ceding dapat dituai","Kalaulah duduk pemangku adat","Banyaklah runding cepat selesai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131478,"Kalau merumput dahan dikerat","Batang dipetik dengan seraga","Kalau menjemput dengan adat","Pulangkan balik dengan lembaga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131479,"Kalau muda pergi di Bantam","Tolong belilah kapur barus","Kalau muda menjadi bantal","Saya menjadi tikar yang halus", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131480,"Kalau mudik ke ujung Batui","Lapis berlapis daun pandannya","Kalau adik berkata begitu","Berapa lapis dinding badannya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131481,"Kalau mufakat sudah putus","Peganglah erat di dalam hati","Kalau itikad sudah lurus","Di situlah tempat hidup dan mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131482,"Kalau mufakat sudah putus","Peganglah erat di dalam hati","Kalau I'tikad sudah tulus","Di situlah tempat hidup dan mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131483,"Kalau na' tahoe diladang padi","Toeroetkan djalan ke Kedjai","Kalau na' tahoe disajang kami","Tjobalah hitoeng kersik di pantai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131484,"Kalau naik pinang masak","Keluar tampuk tinggal mayang","Di timang-timang di letak-letak","Jiwa semangat kasih sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131485,"Kalau nak cari tunang","Pikiaqlah jaoh-jaoh","Bukanlah senang-senang","Akhiaqnya dok begaduh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131486,"Kalau nak gugoq gugoqlah nangka","Jangan menimpa si rantin paoh","Kalau nak tioq tidoqlah mata","Jangan mengenang oghang yang jaoh (Serangkaian Pantun Kedah)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131487,"Kalau nak gugoq gugoqlah kelapa","Jangan menimpa si batang buloh","Kalau nak tidoq tidoqlah mata","Jangan terkenang aghang yang jaoh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131488,"Kalau nak marah bepikiaqlah dulu","Sebelom kata pekara burok","Kalau dah marah terasa malu","Sudah pun malu terasa terok", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (131489,"Kalau nak menikah oghang kaya","Kena belajaq tinggi-tinggi","Kalau Tok Saad nak tunjok bangga","Kena tinggailah botoi todi", 4, 6);
