INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132836,"Kappa' kappa' ngayuhkan tongkang","Daun midding be inggit inggit","Kappa' kappa' hidup membujang","Tali selawwar habir di gigit", 4, 6);
