INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132838,"Kappal nen daree Tandang Sooree","Tienggie haloean daree koorong","Mencharyalah Toean dallam mimpee","Laksana deean dalam tanglong", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132839,"Kapten Light orang lama","Mengenang jasa patung di pahat","Budi baik terbuang percuma","Buat baik di balas jahat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132840,"Kapten Schoe atur bicara","Turut aturan punca teratur","Schutterij teguhkan hati","Jangan kamu bertakut mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132841,"Kapten Schoe bertitah ulang","Sekarang kamu dahulu pulang","Hampir sianglah jam lima","Kami berangkat pergi ke Kema", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132842,"Kapten Schoe sudah katakan","Sekarang kami ada hadapan","Ini titah dari Bangsawan","Ku harap kamu jangan melawan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132843,"Kapten Schoe sudah mengatur","Burger berdiri di muka Kantor","Sambil bilang terima kasih","Sekarang kamilah sudah sampai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132844,"Kapten sudah ulang cerita","Berkata tuan jangan marah","Tuan harus masuk ke kamar","Sesama bapa dan sesaudara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132845,"Kapulau sama kapulau","Kapulau menjaring udang","Banyak kawan tempat bergurau","Tempat menangis jarang saorang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132846,"Ka-puloh gali rengas","Rengas di gali ubi basa","Banyak orang pandang kan mas","Saya tekenang budi dan basa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132847,"Kapuloh mengali ubi","Kebeting layar kan jugak","Sepuloh kali kita berbudi","Kalu miskin terbuang jugak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132848,"Kapuloh mungambil batu","Surabat di dalam kacha","Supuloh di chari takdapat satu","Dapat sobat mundangar kata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132849,"Kapur berukir pinang berlilit","Sirih sesusun bawa ke kota","Irama keroncong alunan mencuit","Di hujung malam tak lelap mata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132850,"Kara kara kuay yang renia","Sukat garing maka bergitu","Idop sangsara lama di dunia","Kurus kring makan tak tentu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132851,"Karakak daun pang gaga","Ber champor dengan ati ati","Niat di ati boleh ada","Seratah pulak mahu menjadi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132852,"Karakok mari rang susun","Tariek pisau balah indayang","Nan tatok uni-lah dusun","Nan risau kumari layang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132853,"Karambia di Tinjau Lauik","Satandan salapan buah","Tangkokkan kami alang lauik","Bunga manyambah jolong babuah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132854,"Kerambil di Tinjau Laut","Setandan delapan buah","Tangkapkan kami elang laut","Bunga menyembah baru berbuah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132855,"Karambia dusun mangatari","Pucuak malepai gudang Cino","Tuan badusun banagari","Tidak bak untuang badan kami", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132856,"Karambia ijau karambia tinggi","Ka ubek urang kasibaran","Maimbau mandeh di nan tinggi","Lai kok raso kadangaran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132857,"Kerambil hijau kerambil tinggi","Untuk ubat orang kesibaran","Mengimbau ibu dari tempat nan tinggi","Adakah serasa kedengaran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132858,"Karambia sabatang tenggi","Babuah sarangkai duo","Hukum adia adat badiri","Pangka saiyo jo sakato", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132859,"Karambia satandan labek","Minyaknyo tangah kuali","Bapasan ka ujan labek","Lai kah sampai to kini", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (132860,"Karambia tandan tumandan","Satandan tidak barisi","Ondeh arumnya bungo pandan","Tingga di jalan ditangisi", 4, 6);
